UPDATE locales_npc_text SET entry = 5257, Text0_0_loc2 = null, Text0_1_loc2 = 'Depuis que mon mari m\'a quittée pour le front, Helene a été mon seul réconfort. Son cœur aimable et son épée aiguisée m\'ont aidée à de nombreuses reprises. Je ne sais pas ce que j\'aurais fait sans elle.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5257;
