UPDATE locales_npc_text SET entry = 5260, Text0_0_loc2 = 'Je sais déjà bien des choses en matière de style. Il s\'agit maintenant de maîtriser les techniques du travail du cuir. La bonne nouvelle, c\'est que Simon m\'aide beaucoup à les apprendre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5260;
UPDATE locales_npc_text SET entry = 5261, Text0_0_loc2 = 'Je ne peux rien vous enseigner, si ce n\'est un sens plus sûr de la couleur et du détail. Allez parler à Simon Tanner, juste là, il connaît de nombreuses techniques avancées du travail du cuir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5261;
UPDATE locales_npc_text SET entry = 5262, Text0_0_loc2 = 'Cette tenue met en valeur le bleu de mes yeux, n\'est-ce pas ? Il est évident que mes mérites sont innombrables, alors je peux bien en partager quelques-uns. Écoutez bien, d\'accord ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5262;
