UPDATE locales_npc_text SET entry = 5265, Text0_0_loc2 = null, Text0_1_loc2 = 'Il faut coudre plus serré, $N. Vos carquois et vos sacs garderont mieux les munitions. Et puis, ça fait négligé sinon...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5265;
UPDATE locales_npc_text SET entry = 5273, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous voulez apprendre à coudre le cuir, vous êtes au bon endroit. Faldron et Telonis sont de véritables artistes, et j\'espère que vous en apprendrez beaucoup avec eux.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5273;
