UPDATE locales_npc_text SET entry = 5275, Text0_0_loc2 = null, Text0_1_loc2 = 'Quel bonheur d\'être ici à Darnassus, au milieu d\'une foule de gens doué ! Je vais emplir ma famille de fierté. Si j\'ai de la chance, les Sentinelles me demanderont de les rejoindre sur le front !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5275;
UPDATE locales_npc_text SET entry = 5276, Text0_0_loc2 = null, Text0_1_loc2 = 'Cette peau n\'est pas prête à être travaillée. J\'aimerais qu\'il ne faille pas tant de temps pour faire du cuir. Il doit y avoir moyen de raccourcir cette attente. Ce n\'est pas bon pour ma santé, vous savez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5276;
UPDATE locales_npc_text SET entry = 5277, Text0_0_loc2 = null, Text0_1_loc2 = 'Ahhh, quelle malchance ! Mes compétences ne peuvent vous être d\'aucune utilité. Allez plutôt voir Fimble Finfuseau, là-bas, près du métier à tisser. Il travaille le cuir depuis plus longtemps que moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5277;
UPDATE locales_npc_text SET entry = 5278, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5278;
UPDATE locales_npc_text SET entry = 5279, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous aimez cette couverture ? J\'ai tué et dépecé la bête moi-même. Que tous les centaures tombent ainsi !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5279;
UPDATE locales_npc_text SET entry = 5280, Text0_0_loc2 = null, Text0_1_loc2 = 'Je n\'ai plus rien à vous apprendre. Parlez à Karolek si vous voulez améliorer vos compétences de Tailleur de cuir.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5280;
UPDATE locales_npc_text SET entry = 5281, Text0_0_loc2 = null, Text0_1_loc2 = 'Dommage que nous ne puissions dépecer les humains et leurs alliés. Toute cette peau va être perdue... Cela dit, qui voudrait de ce cuir fragile pour en faire une protection ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5281;
UPDATE locales_npc_text SET entry = 5282, Text0_0_loc2 = null, Text0_1_loc2 = 'La peau, cette merveille des merveilles ! Elle vous garde tout entier au chaud... Comme vous pouvez le voir, la mienne ne va pas trop bien... Dire que je m\'inquiétais de la taille de mes pores ! Il ne me reste plus guère de pores à regretter aujourd\'hui. D\'un certain point de vue, c\'est une libération.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5282;
UPDATE locales_npc_text SET entry = 5283, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5283;
UPDATE locales_npc_text SET entry = 5284, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5284;
UPDATE locales_npc_text SET entry = 5285, Text0_0_loc2 = 'Viande, peau, ossements... Un bon chasseur ne laisse rien se perdre. Chaque morceau de cuir doit être utilisé, par exemple pour renforcer l\'armure d\'un allié. Seul, chacun de nous est faible, mais ainsi, ensemble, nous sommes invincibles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5285;
UPDATE locales_npc_text SET entry = 5286, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5286;
UPDATE locales_npc_text SET entry = 5287, Text0_0_loc2 = 'Nous vivons et mourons, et la terre demeure, éternelle, inchangée par notre passage. C\'est pourquoi la terre ne saurait nous appartenir, $N. Au contraire, c\'est nous qui lui appartenons, et nous devons la protéger, car sans elle nous n\'avons aucun espoir de survivre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5287;
UPDATE locales_npc_text SET entry = 5288, Text0_0_loc2 = 'Les cavernes en dessous de nous offrent de nombreuses et merveilleuses cachettes, mais vous devez faire attention si vous vous aventurez dans leurs profondeurs, $N. Souvenez-vous que votre propre peau a beaucoup plus de valeur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5288;
UPDATE locales_npc_text SET entry = 5289, Text0_0_loc2 = 'Vous connaissez le cuir mieux que moi. Vous devriez rendre visite au tauren Krulmoo Pleine-lune, au Camp Taurajo, dans les Tarides. Il a bien des connaissances à offrir à un apprenti de votre classe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5289;
UPDATE locales_npc_text SET entry = 5291, Text0_0_loc2 = 'On dit que Cairne lui-même vit ici. Lorsque j\'aurai confectionné une offrande convenable, je partirai à sa recherche. Il aura peut-être des paroles de sagesse à partager avec moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5291;
UPDATE locales_npc_text SET entry = 5292, Text0_0_loc2 = 'Vos talents dépassent les miens, voyez-vous. Allez donc parler à Tarn, qui se trouve juste là, ses connaissances sont plus vastes que les miennes. Je pense qu\'il accueillera volontiers un nouvel apprenti.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5292;
UPDATE locales_npc_text SET entry = 5293, Text0_0_loc2 = 'Lavez-vous les mains avant de commencer tout travail. Maculer le cuir avec du sang ou de la poussière nuirait à sa beauté naturelle.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5293;
UPDATE locales_npc_text SET entry = 5294, Text0_0_loc2 = 'Lorsque j\'ai réussi à m\'extraire de ma tombe, je pensais que ma famille m\'accueillerait à bras ouverts. Mais ils m\'ont chassé du village, hurlant dans une langue que je ne comprenais plus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5294;
