UPDATE locales_npc_text SET entry = 5340, Text0_0_loc2 = 'Ma hache a vu bien des combats et j\'aime l\'avoir à portée de main. Les forces ennemies ont essayé de se frayer un chemin jusqu\'à notre capitale à de nombreuses reprises, mais n\'ayez crainte : nous les avons toujours repoussées.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5340;
UPDATE locales_npc_text SET entry = 5341, Text0_0_loc2 = 'Je ne peux vous offrir une formation plus avancée, car vous travaillez le cuir aussi bien que moi. Essayez du côté d\'Una, là-bas. Je vous recommanderai si besoin est.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5341;
