UPDATE locales_npc_text SET entry = 5344, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5344;
UPDATE locales_npc_text SET entry = 5345, Text0_0_loc2 = 'Actuellement nous n\'avons qu\'un pied sur ce continent, mais les humains ne pourront pas éternellement nous repousser. Ils paieront alors leurs crimes envers nous. Nous couperons leur langue fourchue pour punir leur traîtrise.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5345;
UPDATE locales_npc_text SET entry = 5346, Text0_0_loc2 = 'Je vous enseignerais si je le pouvais, mais vous êtes loin au-dessus de moi. Je vais vous dire : allez parler à la tauren Una, dans les échoppes du nord-ouest du plateau central des Pitons du Tonnerre. Elle a vraiment du talent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5346;
UPDATE locales_npc_text SET entry = 5348, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5348;
UPDATE locales_npc_text SET entry = 5350, Text0_0_loc2 = 'Si j\'ai du travail ? Qu\'est-ce que vous croyez ? Que je reste ici toute la journée à me tourner les pouces ? Si vous êtes ici pour acheter quelque chose, parlez à Handor.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5350;
UPDATE locales_npc_text SET entry = 5351, Text0_0_loc2 = 'Vous n\'apprendrez rien de plus auprès de moi. Vous en savez déjà beaucoup. Si vous voulez progresser, allez voir la tauren Una dans les échoppes du nord-ouest du plateau central des Pitons du Tonnerre. Passez-lui mon bonjour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5351;
UPDATE locales_npc_text SET entry = 5353, Text0_0_loc2 = 'Impossible d\'étudier mes techniques tant que vous ne connaissez pas les bases. Voyez avec Kamari pour entrer en apprentissage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5353;
UPDATE locales_npc_text SET entry = 5354, Text0_0_loc2 = 'Les détours d\'Orgrimmar ont pour but de désorienter les envahisseurs. Vous imaginez à quelle vitesse un pauvre gnome s\'égarerait ici ? Ah ! Si j\'en trouvais un, je le garderais pour m\'en faire un familier.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5354;
UPDATE locales_npc_text SET entry = 5355, Text0_0_loc2 = 'Avant de mourir, j\'étais bibliothécaire. Les guerres et les fléaux m\'étaient totalement étrangers... Je n\'ai pas demandé à devenir ce que je suis. Mais maintenant que j\'ai retrouvé mes esprits, je ne me rendrai pas sans combattre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5355;
UPDATE locales_npc_text SET entry = 5356, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5356;
UPDATE locales_npc_text SET entry = 5358, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5358;
UPDATE locales_npc_text SET entry = 5359, Text0_0_loc2 = 'Je me suis d\'abord établi au quartier des mages, mais c\'était un peu bruyant. Ici, c\'est beaucoup mieux, sauf que certains apprentis acharnés finissent par retrouver ma trace.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5359;
UPDATE locales_npc_text SET entry = 5360, Text0_0_loc2 = null, Text0_1_loc2 = 'Elune a prodigué de nombreux bienfaits aux elfes de la nuit. En retour, notre responsabilité est de préserver ces forêts de la souillure démoniaque que la Horde apporte avec elle.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5360;
UPDATE locales_npc_text SET entry = 5361, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5361;
UPDATE locales_npc_text SET entry = 5363, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5363;
UPDATE locales_npc_text SET entry = 5364, Text0_0_loc2 = null, Text0_1_loc2 = 'Vérifiez toujours vos mesures avant de couper un morceau de cuir. On ne peut se permettre de gaspiller.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5364;
UPDATE locales_npc_text SET entry = 5373, Text0_0_loc2 = 'Vous devez chercher dans les eaux de la marche de l\'Ouest, à l\'ouest-nord-ouest de l\'endroit où la carrière de la Côte de l\'or rejoint le rivage. Une ancre indique l\'endroit où un navire a coulé en eaux profondes. La pression sous-marine y est intense et peut venir à bout du plus solide des nageurs.$B$BPour vous aider, cherchez une formation naturelle, $N, elle abrite une fissure bouillonnante. Les bulles qui s\'en échappent permettent aux plongeurs de reprendre leur souffle.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5373;
UPDATE locales_npc_text SET entry = 5374, Text0_0_loc2 = 'Dans la forêt des Pins argentés, le long de la côte nord, une épave de bateau gît sur la berge. À l\'ouest de l\'épave se trouve ce que vous cherchez. Si vous nagez trop loin, la fatigue aura raison de vous. L\'objet est au fond de l\'océan, dans une boîte verrouillée, attendant que vous vous en empariez.$B$BIl ne sera pas simple de survivre à une plongée dans de telles profondeurs, mais il existe une fissure bouillonnante qui permet aux plongeurs cherchant cette moitié du Pendentif de reprendre leur souffle. Utilisez-la avec sagesse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5374;
UPDATE locales_npc_text SET entry = 5375, Text0_0_loc2 = null, Text0_1_loc2 = 'Au nord de Sombrivage se trouve la Bondissante, une rivière qui provient des terres que nous appelons désormais Gangrebois. Tout comme la rivière coule vers la Lisière des brumes, allez au nord, là où l\'océan se montre le plus profond. Vous trouverez ce que vous cherchez près d\'une formation rocheuse que certains appellent la Griffe de pierre à cause de sa forme.$B$BAttention : les batteurs sont nombreux près de la Griffe de pierre, et ils attaquent tous ceux qui pénètrent sur leur territoire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5375;
UPDATE locales_npc_text SET entry = 5376, Text0_0_loc2 = null, Text0_1_loc2 = 'Au nord-est des Tarides, il existe une étendue d\'eau polluée. Les habitants du coin l\'appellent la Videfange. La KapitalRisk a transformé ces eaux naguère cristallines en un dépotoir de produits chimiques et de déchets industriels. Au cœur de ce marais se trouve ce que vous cherchez, $N.$B$BFaites attention, la KapitalRisk n\'aime pas les intrus et quiconque ne travaille pas pour elle EST un intrus. Vous serez une cible privilégiée en tant que protecteur de la nature. Ils n\'aiment pas trop les gens comme vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5376;
UPDATE locales_npc_text SET entry = 5393, Text0_0_loc2 = 'L\'herbe est douce et luxuriante. L\'air est frais, vibrant de lumière et de chansons. Tout ce que j\'ai vu suggère que Teldrassil est un rêve devenu réalité. Alors pourquoi mon cœur est-il plein de sinistres pressentiments ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5393;
UPDATE locales_npc_text SET entry = 5394, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5394;
UPDATE locales_npc_text SET entry = 5396, Text0_0_loc2 = 'J\'ai peur de ne pouvoir vous aider, $gnoble seigneur:gente dame;, avant que vous ne sachiez mieux traiter le cuir. Parlez plutôt à Faldron ou à Darianna ici présents. Ils peuvent vous apprendre ce que vous désirez savoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5396;
UPDATE locales_npc_text SET entry = 5397, Text0_0_loc2 = 'N\'essayez pas d\'imposer votre volonté au cuir. Laissez ses tendances naturelles dicter quelle forme et quelle fonction il doit prendre. Il ne s\'agit pas d\'une bataille à remporter. Travaillez avec le cuir et non contre lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5397;
UPDATE locales_npc_text SET entry = 5398, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'aiguille est fine, mais le fil est solide et il n\'y a que cette maille qui m\'aille. Notre pacte avec Thrall et les autres tiendra bon. La Horde ne sera pas chassée de ces terres.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5398;
