UPDATE locales_npc_text SET entry = 5425, Text0_0_loc2 = 'Dans nos créations, nous utilisons différentes peaux d\'animaux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5425;
UPDATE locales_npc_text SET entry = 5426, Text0_0_loc2 = 'Je ne suis pas ignorant, mais je suis loin d\'être un expert. Heureusement pour vous, Magar en est un. Il se trouve au fond de l\'échoppe. Voyez s\'il peut vous aider, mais attention à ce que vous dites. Il est irritable.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5426;
