UPDATE locales_npc_text SET entry = 5440, Text0_0_loc2 = 'Salut, qu\'est-ce que je peux faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5440;
UPDATE locales_npc_text SET entry = 5441, Text0_0_loc2 = 'Vous en savez désormais autant que moi ! Il serait sage d\'aller voir Magar à Orgrimmar. Ce n\'est pas le plus jovial des tailleurs, mais il saura vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5441;
UPDATE locales_npc_text SET entry = 5442, Text0_0_loc2 = 'Excellente attitude ! On prend une aiguille et on commence !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5442;
UPDATE locales_npc_text SET entry = 5443, Text0_0_loc2 = 'J\'ai peur de ne plus être ni aimable ni humble. L\'âge et les combats innombrables ont endurci ma chair et mon âme. C\'est peut-être pour cela que Cairne m\'a affecté à la Retraite de Roche-Soleil... Les vents délicats et la chaleur du soleil adoucissent la douleur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5443;
UPDATE locales_npc_text SET entry = 5453, Text0_0_loc2 = 'Salutations. Nos chauve-souris sont bien entraînées et bien nourries. Vous ne trouverez pas plus rapide.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5453;
UPDATE locales_npc_text SET entry = 5454, Text0_0_loc2 = 'Comment puis-je me rendre utile ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5454;
UPDATE locales_npc_text SET entry = 5455, Text0_0_loc2 = 'Salut et bienvenue ! Cela ne se voit peut-être pas, mais faites-moi confiance, c\'est un plaisir pour moi que de vous servir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5455;
UPDATE locales_npc_text SET entry = 5473, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'hippogriffe est une noble bête, fière et rapide. En dresser un est un honneur. Ils peuvent franchir rapidement de grandes distances pour leur cavalier.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5473;
UPDATE locales_npc_text SET entry = 5474, Text0_0_loc2 = 'Pour maintenir la liaison avec le continent, nous avons des vols réguliers d’hippogriffes entre le village de Rut\'theran et Sombrivage.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5474;
UPDATE locales_npc_text SET entry = 5475, Text0_0_loc2 = null, Text0_1_loc2 = 'Il y a beaucoup de beaux poissons au large des côtes de Teldrassil. Si l\'on en croit les rumeurs, ce serait la conséquence de la rapide croissance de notre nouvel Arbre-monde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5475;
UPDATE locales_npc_text SET entry = 5476, Text0_0_loc2 = 'Bien que la côte de Sombrivage soit plus dangereuse que celle de Teldrassil, elle constitue une source de profits pour de nombreux pêcheurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5476;
UPDATE locales_npc_text SET entry = 5477, Text0_0_loc2 = 'C\'est un honneur pour moi de fournir les aventuriers, car ils sont des héros à leur façon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5477;
UPDATE locales_npc_text SET entry = 5479, Text0_0_loc2 = 'Les griffons les plus rapides de ce versant du Nid-de-l\'Aigle. Vous voulez faire un tour ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5479;
UPDATE locales_npc_text SET entry = 5480, Text0_0_loc2 = 'Sur le quai nord, vous pouvez embarquer sur un navire qui vous transportera au village de Rut\'theran et à Darnassus. À partir du quai sud, vous pouvez traverser la Grande mer jusqu\'au port de Hurlevent. Le quai à l\'ouest, au bout de l\'embarcadère, vous conduira sur l\'île de Brume-azur, près de l\'Exodar. Puisse votre voyage se dérouler sans encombre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5480;
UPDATE locales_npc_text SET entry = 5481, Text0_0_loc2 = 'Ce quai ? Vous pouvez attraper le navire qui se rend quelque part chez les elfes de la nuit. Hum, à Auberdine je crois. Je ne suis pas vraiment sûr. Je ne fais que charger et décharger des marchandises.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5481;
UPDATE locales_npc_text SET entry = 5494, Text0_0_loc2 = 'Livrer ces tonneaux au site de fouilles n\'est pas une tâche facile, mais ils comptent vraiment sur nous pour obtenir la poudre à canon.$B$BNous devons faire tout ce que nous pouvons contre les Sombrefer !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5494;
UPDATE locales_npc_text SET entry = 5495, Text0_0_loc2 = 'Mes coursiers du vent sont entraînés pour voler rapidement à travers l\'air chaud des Tarides.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5495;
UPDATE locales_npc_text SET entry = 5496, Text0_0_loc2 = 'Les Tarides, avec leur soleil écrasant et leurs habitants hostiles, est l\'endroit parfait pour y éprouver votre force et votre endurance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5496;
UPDATE locales_npc_text SET entry = 5497, Text0_0_loc2 = 'Salutations. Si vous cherchez des produits en cuir, vous verrez que mes objets sont admirablement réalisés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5497;
UPDATE locales_npc_text SET entry = 5498, Text0_0_loc2 = 'Êtes-vous ici pour aider à la livraison ? Miran a presque fini de se préparer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5498;
UPDATE locales_npc_text SET entry = 5500, Text0_0_loc2 = 'Quand on monte un coursier du vent, on comprend vite qu\'il n\'a pas usurpé sa réputation de force et de rapidité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5500;
UPDATE locales_npc_text SET entry = 5501, Text0_0_loc2 = 'C\'est dangereux, comme activité !$B$BMiran vient de partir avec un colis. Il devrait être de retour d’ici quelques minutes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5501;
UPDATE locales_npc_text SET entry = 5513, Text0_0_loc2 = 'Vous surpassez mes aptitudes, vous aurez besoin de quelqu\'un de plus expérimenté que moi. Je dirais le docteur Herbert Halsey, un mort-vivant qui travaille à l\'Apothicarium d\'Fossoyeuse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5513;
UPDATE locales_npc_text SET entry = 5515, Text0_0_loc2 = 'Jetez un œil à ma nourriture, l\'$gami:amie;. Vous ne voudriez pas partir dans les Tarides sans un bon stock de provisions ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5515;
UPDATE locales_npc_text SET entry = 5516, Text0_0_loc2 = 'Tant que vous n\'avez pas observé le monde en chevauchant un coursier du vent, vous ne savez pas ce que vivre signifie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5516;
UPDATE locales_npc_text SET entry = 5517, Text0_0_loc2 = 'Vous trouverez un passage vers Theramore depuis ce dock. Mais écoutez-moi bien : il est dangereux de traverser ces terres pour les membres de l\'Alliance. Protégez vos arrières.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5517;
UPDATE locales_npc_text SET entry = 5519, Text0_0_loc2 = 'Bonjour.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5519;
UPDATE locales_npc_text SET entry = 5520, Text0_0_loc2 = 'Vos compétences dépassent largement les miennes. Allez plutôt voir Georgio Bolero, le patron des Textiles Duncan. C\'est à deux pas, et il pourra vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5520;
UPDATE locales_npc_text SET entry = 5522, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5522;
UPDATE locales_npc_text SET entry = 5523, Text0_0_loc2 = 'Une belle démonstration de mérite. Votre instruction peut désormais continuer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5523;
UPDATE locales_npc_text SET entry = 5524, Text0_0_loc2 = 'Ah ! Ce tissu de Darnassia est d’une telle délicatesse !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5524;
UPDATE locales_npc_text SET entry = 5525, Text0_0_loc2 = 'N\'est-ce pas vous l\'$gexpert:experte; ? Vous en savez plus que moi, je vous assure. N\'attrapez pas la grosse tête, mais si vous voulez continuer à progresser, parlez à Georgio Bolero, à Hurlevent. Il paraît qu\'on ne peut le surpasser !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5525;
UPDATE locales_npc_text SET entry = 5527, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5527;
UPDATE locales_npc_text SET entry = 5528, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5528;
UPDATE locales_npc_text SET entry = 5529, Text0_0_loc2 = null, Text0_1_loc2 = 'Entendez-vous l\'appel de la chasse, $c ? Vous devez écouter attentivement, l\'appel vient des profondeurs de votre être. De notre être à tous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5529;
UPDATE locales_npc_text SET entry = 5530, Text0_0_loc2 = 'J\'ai fabriqué beaucoup des choses que vous avez vues dans la boutique. Qu\'en pensez-vous ? Répondez sagement, votre vie en dépend.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5530;
UPDATE locales_npc_text SET entry = 5531, Text0_0_loc2 = 'Vous avez besoin d\'un maître qui en sait plus que moi. Allez voir Josef Gregorian à Fossoyeuse. Eh bien ! Que faites-vous encore là ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5531;
UPDATE locales_npc_text SET entry = 5533, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5533;
UPDATE locales_npc_text SET entry = 5534, Text0_0_loc2 = 'Il était temps que vous montriez des résultats ! À moi, maintenant !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5534;
