UPDATE locales_npc_text SET entry = 5561, Text0_0_loc2 = 'Oui, je tiens cette échoppe depuis quelque temps maintenant. J\'ai fait de bonnes affaires dernièrement... Il y a pas mal de nouveaux aventuriers dans les parages...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5561;
UPDATE locales_npc_text SET entry = 5562, Text0_0_loc2 = 'Je ne connais qu\'une personne qui soit plus instruite que moi dans l\'art du Tailleur. La dernière fois que j\'ai eu de ses nouvelles, il se trouvait sur l\'île de Theramore. C\'est cette personne que vous devriez chercher.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5562;
UPDATE locales_npc_text SET entry = 5564, Text0_0_loc2 = 'Vous aurez besoin d\'entraînement avant que je puisse vous faire partager mon savoir, j\'en ai bien peur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5564;
UPDATE locales_npc_text SET entry = 5565, Text0_0_loc2 = 'Bien ! Je peux dire que mes efforts n\'auront pas été vains !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5565;
UPDATE locales_npc_text SET entry = 5566, Text0_0_loc2 = 'Les étoffes, c\'est pas mal. Personnellement, je préfère travailler la peau humaine.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5566;
UPDATE locales_npc_text SET entry = 5567, Text0_0_loc2 = 'Vous avez besoin de l\'enseignement d\'un véritable maître ! Allez voir Daryl Stack à Moulin-de-Tarren, et prenez votre destin à bras-le-corps !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5567;
UPDATE locales_npc_text SET entry = 5569, Text0_0_loc2 = 'Je vous instruirai quand l\'heure sera venue... Et ce n\'est pas encore le cas. Vous avez encore beaucoup à apprendre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5569;
