UPDATE locales_npc_text SET entry = 5575, Text0_0_loc2 = 'Cet endroit est un peu venteux... mais qu\'importe ! Je ne suis plus de ce monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5575;
UPDATE locales_npc_text SET entry = 5577, Text0_0_loc2 = 'Je dois reconnaître que vous vous débrouillez bien dans l\'art du Tailleur. Maintenant, voyons ce que vous valez RÉELLEMENT...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5577;
UPDATE locales_npc_text SET entry = 5578, Text0_0_loc2 = 'Qui vous envoie ici ? Vous n\'êtes pas à la hauteur de mes instructions ! On vous le fera savoir quand vous mériterez de travailler avec moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5578;
UPDATE locales_npc_text SET entry = 5579, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'aime à m\'asseoir ici et à écouter la musique de la Grande mer. Oh, pardon ? Oui, il y a un navire que vous pouvez prendre jusqu\'au port de Menethil. Il s\'ancre ici de temps en temps.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5579;
UPDATE locales_npc_text SET entry = 5580, Text0_0_loc2 = 'Croyez-moi ! Vous ne trouverez pas plus rapides griffons dans tous les Royaumes de l\'est que ceux de Thelsamar !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5580;
UPDATE locales_npc_text SET entry = 5581, Text0_0_loc2 = 'Si vous voulez savoir, mineur est la plus belle des professions. Et si vous voulez savoir, les meilleurs mineurs sont les nains.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5581;
UPDATE locales_npc_text SET entry = 5582, Text0_0_loc2 = 'Les nains Marteaux-hardis ont certes des montures rapides, mais est-ce que ces griffons du Nid-de-l\'Aigle peuvent résister à la chaleur de la Grande Forge ? Je ne pense pas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5582;
UPDATE locales_npc_text SET entry = 5583, Text0_0_loc2 = 'Pour être mineur, il faut une poigne solide, un cœur bien accroché et une bonne couche de suie dans les cheveux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5583;
UPDATE locales_npc_text SET entry = 5584, Text0_0_loc2 = 'La caisse solide se trouve au milieu de la scierie entourée par des débris et de fervents disciples de la Croisade écarlate.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5584;
UPDATE locales_npc_text SET entry = 5593, Text0_0_loc2 = null, Text0_1_loc2 = 'Cet ours suit une voie proche de la Perce des Serres, à l\'ouest du Lac Mystral et de l\'Avant-poste que l\'Alliance a placé au Refuge de Vent-argent. C\'est la moins terrible des trois créatures que vous affronterez dans la Chasse d\'Orneval, mais ne baissez tout de même pas votre garde. Sa présence est une menace permanente pour les voyageurs qui vont d\'Orneval aux Serres-Rocheuses.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5593;
