UPDATE locales_npc_text SET entry = 5595, Text0_0_loc2 = null, Text0_1_loc2 = 'On dit que ce sabre-de-nuit est docile en présence des elfes de la nuit et de leurs alliés, ce qui en fait un adversaire encore plus valable. Le territoire de chasse de Shadumbra va de la Retraite de Raynebois, à l\'ouest d\'ici, jusqu\'à loin au ponant. Sa fourrure est noire comme une galaxie morte et il ne faut pas sous-estimer sa rapidité.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5595;
UPDATE locales_npc_text SET entry = 5613, Text0_0_loc2 = 'Salutations, $c. Bienvenue sur le front, dans notre combat pour la conquête d\'Orneval ! Si vous cherchez quelque chose à faire, vous êtes au bon endroit ! Je suis toujours à la recherche d\'individus capables de m\'aider à accroître la gloire de la Horde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5613;
