UPDATE locales_npc_text SET entry = 5653, Text0_0_loc2 = 'Si vous cherchez des informations sur les déplacements des caravanes et des marchands au poste de Bois-brisé, alors je suis l\'orc de la situation. Malheureusement pour vous, je n\'aime pas les gens.$B$BAlors soyez $gbref:brève;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5653;
UPDATE locales_npc_text SET entry = 5654, Text0_0_loc2 = 'Les éléments nous parlent, $C. Ils partagent les connaissances que nous devons apprendre et les secrets qui doivent être gardés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5654;
UPDATE locales_npc_text SET entry = 5673, Text0_0_loc2 = 'À travers un vigoureux recyclage, j\'ai dû contraindre bien des étudiants à oublier ce qu\'ils avaient appris, afin qu\'ils soient prêts à recevoir mon enseignement. Le service que je propose devient de plus en plus difficile à accomplir. C\'est pour cela que c\'est sans cesse plus cher. Voulez-vous que je vous fasse oublier ce que vous avez appris ?', Text0_1_loc2 = 'À travers un vigoureux recyclage, j\'ai dû contraindre bien des étudiants à oublier ce qu\'ils avaient appris, afin qu\'ils soient prêts à recevoir mon enseignement. Le service que je propose devient de plus en plus difficile à accomplir. C\'est pour cela que c\'est sans cesse plus cher. Voulez-vous que je vous fasse oublier ce que vous avez appris ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5673;
UPDATE locales_npc_text SET entry = 5674, Text0_0_loc2 = 'Si vous le voulez, en plus de l\'enseignement que je propose, j\'ai le pouvoir de faire disparaître de votre esprit la connaissance de vos talents. Sachez qu\'à chaque fois que vous subissez cette procédure elle est plus difficile à accomplir, et donc plus onéreuse.', Text0_1_loc2 = 'Si vous le voulez, en plus de l\'enseignement que je propose, j\'ai le pouvoir de faire disparaître de votre esprit la connaissance de vos talents. Sachez qu\'à chaque fois que vous subissez cette procédure elle est plus difficile à accomplir, et donc plus onéreuse.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5674;
UPDATE locales_npc_text SET entry = 5693, Text0_0_loc2 = 'Les arcanes ne corrompent que les faibles. Continuez votre formation ou vous connaîtrez un sort similaire.', Text0_1_loc2 = 'Les arcanes ne corrompent que les faibles. Continuez votre formation ou vous connaîtrez un sort similaire.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5693;
UPDATE locales_npc_text SET entry = 5694, Text0_0_loc2 = 'Bienvenue, $gmon frère:ma sœur;. Si vous êtes ici pour apprendre, j\'aurai grand plaisir à vous enseigner.$B$BBientôt, vous entendrez l\'appel du Rêve d\'émeraude. Vous hibernerez pendant des années et marcherez parmi les formes les plus pures. C\'est là que votre ultime formation aura lieu.', Text0_1_loc2 = 'Bienvenue, $gmon frère:ma sœur;. Si vous êtes ici pour apprendre, j\'aurai grand plaisir à vous enseigner.$B$BBientôt, vous entendrez l\'appel du Rêve d\'émeraude. Vous hibernerez pendant des années et marcherez parmi les formes les plus pures. C\'est là que votre ultime formation aura lieu.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5694;
UPDATE locales_npc_text SET entry = 5695, Text0_0_loc2 = 'Que puis-je faire pour vous ?', Text0_1_loc2 = 'Que puis-je faire pour vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5695;
UPDATE locales_npc_text SET entry = 5696, Text0_0_loc2 = 'Je n\'ai plus rien à enseigner ! Pas à quelqu\'un qui est de mon niveau, comme vous ! Vous autres, les gens de la Horde, vous devez aller parler à Nogg, à Orgrimmar !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5696;
UPDATE locales_npc_text SET entry = 5713, Text0_0_loc2 = null, Text0_1_loc2 = 'Je ne vous veux pas de mal. J\'enquête sur les hurans et je prends des notes sur leurs mœurs. Mon peuple a remarqué quelque chose d\'étrange, une sorte d\'émanation maléfique, en provenance de cet endroit. Je suis là pour en apprendre plus, et ce que j\'ai appris est inquiétant.$B$BIl semble qu\'il y ait dans les parages un diplomate mort-vivant versé dans les arts des arcanes. Je ne crois pas que ce soit un des Réprouvés... Je pense que c\'est pire que ça.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5713;
UPDATE locales_npc_text SET entry = 5714, Text0_0_loc2 = 'Vous en savez assez pour représenter un danger, mais je crois que vous avez encore besoin de vous perfectionner, $N.', Text0_1_loc2 = 'Vous en savez assez pour représenter un danger, mais je crois que vous avez encore besoin de vous perfectionner, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5714;
UPDATE locales_npc_text SET entry = 5715, Text0_0_loc2 = 'Votre volonté n\'est pas assez puissante pour manipuler les forces que nous contrôlons, $c.', Text0_1_loc2 = 'Votre volonté n\'est pas assez puissante pour manipuler les forces que nous contrôlons, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5715;
UPDATE locales_npc_text SET entry = 5716, Text0_0_loc2 = '$N, en quoi puis-je approfondir votre formation aux yeux de la Terre-mère ?', Text0_1_loc2 = '$N, en quoi puis-je approfondir votre formation aux yeux de la Terre-mère ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5716;
UPDATE locales_npc_text SET entry = 5717, Text0_0_loc2 = 'Les voies de la Terre-mère sont innombrables. Que vos ancêtres vous gardent, $c.', Text0_1_loc2 = 'Les voies de la Terre-mère sont innombrables. Que vos ancêtres vous gardent, $c.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5717;
UPDATE locales_npc_text SET entry = 5719, Text0_0_loc2 = 'Il est bon que vous vous humiliiez devant moi, $N, si vous voulez que je vous aide à devenir un meilleur démoniste.', Text0_1_loc2 = 'Il est bon que vous vous humiliiez devant moi, $N, si vous voulez que je vous aide à devenir un meilleur démoniste.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5719;
UPDATE locales_npc_text SET entry = 5720, Text0_0_loc2 = 'Je n\'ai pas de temps à perdre avec les gens de votre espèce, $C.', Text0_1_loc2 = 'Je n\'ai pas de temps à perdre avec les gens de votre espèce, $C.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5720;
UPDATE locales_npc_text SET entry = 5721, Text0_0_loc2 = 'Rien qu\'à votre posture, je peux dire que vous n\'êtes pas un guerrier.$B$BAllez plutôt vous livrer à quelque activité sans danger... Lire un livre, ce genre de choses...', Text0_1_loc2 = 'Rien qu\'à votre posture, je peux dire que vous n\'êtes pas un guerrier.$B$BAllez plutôt vous livrer à quelque activité sans danger... Lire un livre, ce genre de choses...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5721;
UPDATE locales_npc_text SET entry = 5722, Text0_0_loc2 = 'Les ténèbres vous rejettent, $c. Cessez de me casser les oreilles et sortez de ma vue. Disparaissez !', Text0_1_loc2 = 'Les ténèbres vous rejettent, $c. Cessez de me casser les oreilles et sortez de ma vue. Disparaissez !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5722;
UPDATE locales_npc_text SET entry = 5723, Text0_0_loc2 = 'J\'ai bien peur que votre quotient de ténèbres ne soit pas suffisant pour mériter que je discute plus longtemps avec vous. Passez votre chemin.', Text0_1_loc2 = 'J\'ai bien peur que votre quotient de ténèbres ne soit pas suffisant pour mériter que je discute plus longtemps avec vous. Passez votre chemin.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5723;
UPDATE locales_npc_text SET entry = 5724, Text0_0_loc2 = 'Vous n\'êtes pas un guerrier... $gLe dernier:La dernière:c; $c que j\'ai entraîné n\'a même pas tenu une journée.$B$BRegardez-moi ça, encore $gun gros malin:une petite futée; qui se prend pour un guerrier... Ah !', Text0_1_loc2 = 'Vous n\'êtes pas un guerrier... $gLe dernier:La dernière:c; $c que j\'ai entraîné n\'a même pas tenu une journée.$B$BRegardez-moi ça, encore $gun gros malin:une petite futée; qui se prend pour un guerrier... Ah !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5724;
UPDATE locales_npc_text SET entry = 5725, Text0_0_loc2 = 'Je ne sens pas en vous l\'esprit du guerrier, mon enfant. Il n\'est chez moi aucune connaissance qui puisse profiter à $gun:une:c; $c $gtel:telle:c; que vous.', Text0_1_loc2 = 'Je ne sens pas en vous l\'esprit du guerrier, mon enfant. Il n\'est chez moi aucune connaissance qui puisse profiter à $gun:une:c; $c $gtel:telle:c; que vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5725;
UPDATE locales_npc_text SET entry = 5733, Text0_0_loc2 = 'La Main brisée a été capable de placer des espions dans la citadelle supérieure de Rochenoire. Ils n\'ont pas réussi à rassembler bien vite leurs informations, mais nous avons fait des découvertes qui ne peuvent être ignorées.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5733;
UPDATE locales_npc_text SET entry = 5734, Text0_0_loc2 = 'Il semble que l\'influence des dragons noirs ait dépassé les frontières des Steppes ardentes pour pénétrer dans Hurlevent ! Depuis, l\'usurpateur a été démasqué et chassé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5734;
UPDATE locales_npc_text SET entry = 5735, Text0_0_loc2 = 'En effet. La mère des dragons noirs a utilisé sa maîtrise des illusions pour prendre l\'apparence d\'un membre de la cour royale. Pire, elle avait pris le contrôle du généralissime de Hurlevent, Bolvar Fordragon.$B$BOn comprend mieux pourquoi des décisions stratégiques aussi ineptes ont été prises. Ces temps-ci, les manœuvres des forces de l\'Alliance nous ont laissés perplexes. Nous savons aujourd\'hui pourquoi les dragons noirs essayaient de diviser l\'empire : pour le détruire de l\'intérieur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5735;
UPDATE locales_npc_text SET entry = 5736, Text0_0_loc2 = '<Thrall rit.>$B$BVous avez encore beaucoup à apprendre, $R.$B$BVous êtes trop jeune pour vous souvenir des guerres et des bains de sang du passé. Trop jeune pour vous rappeler la malédiction qui a frappé notre peuple.$B$BLa paix actuelle avec l\'Alliance est fragile. Il y a des escarmouches et des petits combats, mais la paix demeure, une situation que notre peuple n\'a pas connue depuis des millénaires. Voulez-vous abandonner tout cela et nous plonger à nouveau dans le chaos, contre un nouvel ennemi ? Un qui ne connaîtrait pas le repos avant de nous avoir tous tués ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5736;
UPDATE locales_npc_text SET entry = 5737, Text0_0_loc2 = 'Non, $N, effectivement. Mais je ne vous blâme pas, car votre esprit est proche du mien quand j\'étais jeune : impétueux, rebelle, et souvent incohérent ! Mais ce sont là des traits de caractère que je ne peux plus me permettre.$B$BAssez pleuré, $R, j\'ai une tâche pour vous. Une tâche à laquelle vous pouvez ne pas survivre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5737;
UPDATE locales_npc_text SET entry = 5738, Text0_0_loc2 = '<Thrall rit.>$B$BJ\'en prends bonne note... Maintenant écoutez-moi, $N. La mère des dragons noirs, Onyxia, a été chassée de sa position dominante à Hurlevent. Elle s\'est échappée, volant jusqu\'à son antre du marécage d\'Âprefange.$B$BOn ne peut permettre sa présence si près de nos villes principales. Nous devons frapper avant d\'être frappés !$B$BJe veux qu\'elle soit détruite, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5738;
