UPDATE locales_npc_text SET entry = 5740, Text0_0_loc2 = 'Bienvenue. Si vous êtes ici pour apprendre, ce sera un honneur pour moi de vous y aider.$B$BNous sommes les gardiens de l\'équilibre, aujourd\'hui et à jamais, tandis que Malfurion est plongé dans le Rêve. Ne l\'oubliez jamais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5740;
