UPDATE locales_npc_text SET entry = 5773, Text0_0_loc2 = 'Les Serres-Rocheuses sont truffées de cavernes dangereuses. Les explorer offre bien des aventures à ceux qui sont prêts à risquer leur vie pour la gloire.$B$B$C, peut-être cherchez-vous des richesses que seuls votre cœur et votre âme peuvent vous donner.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5773;
