UPDATE locales_npc_text SET entry = 5834, Text0_0_loc2 = 'La Confrérie du thorium dispose des connaissances nécessaires pour forger les bras sacrés et l\'armement du seigneur du Feu, mais nous n\'avons pas la main-d\'œuvre pour collecter les matériaux indispensables.$B$BMoi, Lokhtos Sombrescompte, j\'ai été chargé par la confrérie d\'échanger les recettes contre du matériau en provenance du Cœur du Magma.$B$BPrésentez votre offre, $r.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5834;
UPDATE locales_npc_text SET entry = 5835, Text0_0_loc2 = 'Vous voulez que vos démons soient plus puissants ? Cela vous coûtera cher, mais vous êtes au bon endroit.', Text0_1_loc2 = 'Vous voulez que vos démons soient plus puissants ? Cela vous coûtera cher, mais vous êtes au bon endroit.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5835;
UPDATE locales_npc_text SET entry = 5836, Text0_0_loc2 = 'Vous n\'êtes pas démoniste et je n\'ai pas de temps pour vous. Filez !', Text0_1_loc2 = 'Vous n\'êtes pas démoniste et je n\'ai pas de temps pour vous. Filez !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5836;
UPDATE locales_npc_text SET entry = 5838, Text0_0_loc2 = 'Vous êtes là pour une formation qui vous permettrait de fortifier vos familiers ?', Text0_1_loc2 = 'Vous êtes là pour une formation qui vous permettrait de fortifier vos familiers ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5838;
UPDATE locales_npc_text SET entry = 5839, Text0_0_loc2 = 'Ah ! Je n\'aide que les chasseurs et leurs familiers.', Text0_1_loc2 = 'Ah ! Je n\'aide que les chasseurs et leurs familiers.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5839;
UPDATE locales_npc_text SET entry = 5840, Text0_0_loc2 = 'La Société royale des apothicaires n\'a guère de patience envers les imbéciles. Peut-être que la prochaine fois que nous parlerons de mes montures, vous serez $gconsidéré:considérée; comme $gexalté:exaltée; à Fossoyeuse. Jusque-là...', Text0_1_loc2 = 'La Société royale des apothicaires n\'a guère de patience envers les imbéciles. Peut-être que la prochaine fois que nous parlerons de mes montures, vous serez $gconsidéré:considérée; comme $gexalté:exaltée; à Fossoyeuse. Jusque-là...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5840;
