UPDATE locales_npc_text SET entry = 5855, Text0_0_loc2 = 'Ah non, je ne désire pas vendre de cheval à quelqu\'un qui n\'est pas considéré au moins comme exalté par le royaume de Hurlevent. Ces montures sont les meilleures que vous trouverez, nous ne voulons pas qu\'ils finissent entre les mains de l\'ennemi.$B$BJe ne dis pas que vous êtes hostile, bien sûr... Mais gagnez la confiance de Hurlevent et nous pourrons parler affaires.', Text0_1_loc2 = 'Ah non, je ne désire pas vendre de cheval à quelqu\'un qui n\'est pas considéré au moins comme exalté par le royaume de Hurlevent. Ces montures sont les meilleures que vous trouverez, nous ne voulons pas qu\'ils finissent entre les mains de l\'ennemi.$B$BJe ne dis pas que vous êtes hostile, bien sûr... Mais gagnez la confiance de Hurlevent et nous pourrons parler affaires.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5855;
