UPDATE locales_npc_text SET entry = 5919, Text0_0_loc2 = null, Text0_1_loc2 = 'Entre la lumière et les ténèbres, il y a des ombres... Des ombres qui ont des pouvoirs dont vous n\'avez pas idée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5919;
UPDATE locales_npc_text SET entry = 5920, Text0_0_loc2 = 'Vous ne trouverez pas de meilleur kébab de rat de ce côté de Khaz Modan !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5920;
UPDATE locales_npc_text SET entry = 5921, Text0_0_loc2 = 'Vous avez l\'air de vouloir du travail, alors je ne saurais trop vous conseiller de prendre le tram et d\'aller visiter mon frère, Monty, au dépôt du tram des profondeurs de Forgefer. Je pense qu\'il aura quelque chose à vous faire faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5921;
UPDATE locales_npc_text SET entry = 5933, Text0_0_loc2 = 'Faites attention à vos arrières...', Text0_1_loc2 = null, Text1_0_loc2 = 'Le manoir de Ravenholdt ne vous reconnaît pas, $C.', Text1_1_loc2 = null, Text2_0_loc2 = 'De l\'impertinence. Maître Ravenholdt n\'aime pas ça.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5933;
UPDATE locales_npc_text SET entry = 5934, Text0_0_loc2 = 'Quel plaisir de vous revoir, $N.', Text0_1_loc2 = null, Text1_0_loc2 = 'Salutations, $C. Avez-vous parlé à Fahrad ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Le Syndicat est composé de politicards désorganisés, sans talent, sans courage...', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5934;
UPDATE locales_npc_text SET entry = 5938, Text0_0_loc2 = 'Vous osez poser le pied dans le manoir de Ravenholdt, limace ?', Text0_1_loc2 = 'Vous osez poser le pied dans le manoir de Ravenholdt, limace ?', Text1_0_loc2 = 'Réussir à vous en sortir sans un couteau entre les omoplates ou du poison dans les veines serait un véritable exploit !', Text1_1_loc2 = 'Réussir à vous en sortir sans un couteau entre les omoplates ou du poison dans les veines serait un véritable exploit !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5938;
UPDATE locales_npc_text SET entry = 5939, Text0_0_loc2 = 'Vous progressez de belle manière, $N. Les échos sont fort positifs.', Text0_1_loc2 = 'Vous progressez de belle manière, $N. Les échos sont fort positifs.', Text1_0_loc2 = 'Vous devriez parler à Winstone. Il paraît qu\'il cherche des voleurs ayant vos compétences.', Text1_1_loc2 = 'Vous devriez parler à Winstone. Il paraît qu\'il cherche des voleurs ayant vos compétences.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5939;
UPDATE locales_npc_text SET entry = 5943, Text0_0_loc2 = null, Text0_1_loc2 = 'Qu\'Elune marche à vos côtés, $c. Le Temple de la lune vient à l\'aide des citoyens perdus d\'Auberdine.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5943;
UPDATE locales_npc_text SET entry = 5973, Text0_0_loc2 = 'Shoja\'my est par là, devant l\'auberge. Circulez, je n\'ai pas de temps à perdre.', Text0_1_loc2 = 'Shoja\'my est par là, devant l\'auberge. Circulez, je n\'ai pas de temps à perdre.', Text1_0_loc2 = 'Vous avez des familiers dont vous voulez que l\'on s\'occupe ? Veillez à ce qu\'ils soient bien propres ! Et cherchez Shoja\'my devant l\'auberge.', Text1_1_loc2 = 'Vous avez des familiers dont vous voulez que l\'on s\'occupe ? Veillez à ce qu\'ils soient bien propres ! Et cherchez Shoja\'my devant l\'auberge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5973;
UPDATE locales_npc_text SET entry = 5974, Text0_0_loc2 = 'Allez à la Vallée de l\'Honneur et cherchez Xon\'cha, près des écuries.', Text0_1_loc2 = 'Allez à la Vallée de l\'Honneur et cherchez Xon\'cha, près des écuries.', Text1_0_loc2 = 'Xon\'cha s\'occupera de vos autres familiers. Vous le trouverez dans la Vallée de l\'Honneur, aux écuries situées entre l\'arène et le Hall du chasseur. Partez, maintenant !', Text1_1_loc2 = 'Xon\'cha s\'occupera de vos autres familiers. Vous le trouverez dans la Vallée de l\'Honneur, aux écuries situées entre l\'arène et le Hall du chasseur. Partez, maintenant !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5974;
UPDATE locales_npc_text SET entry = 5976, Text0_0_loc2 = 'Ah oui, c\'est Seikwa que vous devez trouver. La dernière fois que je l\'ai vue, elle était devant l\'auberge.', Text0_1_loc2 = 'Ah oui, c\'est Seikwa que vous devez trouver. La dernière fois que je l\'ai vue, elle était devant l\'auberge.', Text1_0_loc2 = 'Seikwa pourra s\'occuper de vos animaux quand vous en aurez besoin. Cherchez-la devant l\'auberge.', Text1_1_loc2 = 'Seikwa pourra s\'occuper de vos animaux quand vous en aurez besoin. Cherchez-la devant l\'auberge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5976;
UPDATE locales_npc_text SET entry = 5977, Text0_0_loc2 = 'C\'est Bulrug qui pourra vous aider. Vous le trouverez à l\'extérieur de la Côte inférieure, entre l\'auberge et la banque des Pitons du Tonnerre.', Text0_1_loc2 = 'C\'est Bulrug qui pourra vous aider. Vous le trouverez à l\'extérieur de la Côte inférieure, entre l\'auberge et la banque des Pitons du Tonnerre.', Text1_0_loc2 = 'L\'intérêt que vous portez au bien-être de vos compagnons animaux est digne d\'éloges. Bulrug, sur la Côte inférieure, pourra vous prêter la main. Cherchez-le entre la banque et l\'auberge.', Text1_1_loc2 = 'L\'intérêt que vous portez au bien-être de vos compagnons animaux est digne d\'éloges. Bulrug, sur la Côte inférieure, pourra vous prêter la main. Cherchez-le entre la banque et l\'auberge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5977;
