UPDATE locales_npc_text SET entry = 5979, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5979;
UPDATE locales_npc_text SET entry = 5980, Text0_0_loc2 = 'Vous pouvez faire confiance à Alassin pour s\'occuper de vos familiers. Il vit dans l\'Enclave cénarienne, là où les chasseurs se réunissent.', Text0_1_loc2 = 'Vous pouvez faire confiance à Alassin pour s\'occuper de vos familiers. Il vit dans l\'Enclave cénarienne, là où les chasseurs se réunissent.', Text1_0_loc2 = 'Alassin s\'est occupé des animaux de nos chasseurs pendant des siècles. Ses soins ont excellente réputation auprès des chasseurs de la région. Si vous le cherchez également, vous le trouverez dans l\'Enclave cénarienne.', Text1_1_loc2 = 'Alassin s\'est occupé des animaux de nos chasseurs pendant des siècles. Ses soins ont excellente réputation auprès des chasseurs de la région. Si vous le cherchez également, vous le trouverez dans l\'Enclave cénarienne.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5980;
UPDATE locales_npc_text SET entry = 5981, Text0_0_loc2 = 'Nous sommes les pièces d\'un immense puzzle, $N. Tout comme on pousse la pièce d\'un puzzle vers sa juste place, nous sommes guidés par un autre.$B$BEt peut-être ceux qui nous guident sont-ils eux-mêmes guidés par d\'autres, plus puissants encore.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5981;
UPDATE locales_npc_text SET entry = 5982, Text0_0_loc2 = 'Ah, douce Seriadne ! Tous les animaux sont en sûreté entre ses mains. Vous la trouverez près de l\'auberge, à Dolanaar.', Text0_1_loc2 = 'Ah, la gente Seriadne. Tous les animaux sont entre de bonnes mains sous sa protection. Vous la trouverez à l\'entrée de l\'auberge, dans Dolanaar.', Text1_0_loc2 = 'Vous cherchez quelqu\'un qui sait soigner les animaux ? Alors parlez à Seriadne, devant l\'auberge de Dolanaar. C\'est elle que vous cherchez.', Text1_1_loc2 = 'Vous cherchez quelqu\'un qui sait soigner les animaux ? Alors parlez à Seriadne, devant l\'auberge de Dolanaar. C\'est elle que vous cherchez.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5982;
UPDATE locales_npc_text SET entry = 5983, Text0_0_loc2 = 'Cette bonne vieille Erma, voilà des années qu\'elle s\'occupe des animaux. Elle sera sûrement ravie de prendre soin de ceux que vous pourriez lui confier. Vous la trouverez devant l\'auberge de la Fierté du lion.', Text0_1_loc2 = 'Cette bonne vieille Erma, voilà des années qu\'elle s\'occupe des animaux. Elle sera sûrement ravie de prendre soin de ceux que vous pourriez lui confier. Vous la trouverez devant l\'auberge de la Fierté du lion.', Text1_0_loc2 = 'Erma s\'occupe des soins des animaux. Elle se trouve devant l\'auberge de la Fierté du lion si vous avez besoin de lui parler.', Text1_1_loc2 = 'Erma s\'occupe des soins des animaux. Elle se trouve devant l\'auberge de la Fierté du lion si vous avez besoin de lui parler.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5983;
UPDATE locales_npc_text SET entry = 5984, Text0_0_loc2 = 'Jenova Targe-pierre, dans le quartier des nains, est la personne que vous cherchez. Elle s\'occupe des familiers des chasseurs quand ils se rendent chez son père pour y poursuivre leur formation.', Text0_1_loc2 = 'Jenova Targe-pierre, dans le quartier des nains, est la personne que vous cherchez. Elle s\'occupe des familiers des chasseurs quand ils se rendent chez son père pour y poursuivre leur formation.', Text1_0_loc2 = 'Si vous avez besoin d\'abriter votre familier pendant que vous en formez un autre, parlez à Jenova Targe-pierre, dans le quartier des nains.', Text1_1_loc2 = 'Si vous avez besoin d\'abriter votre familier pendant que vous en formez un autre, parlez à Jenova Targe-pierre, dans le quartier des nains.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5984;
UPDATE locales_npc_text SET entry = 5985, Text0_0_loc2 = 'Eh bien, vous trouverez Shelby Pierre-à-feu à la sortie de la Distillerie Tonnebière, dans Kharanos. Elle s\'occupera bien de tous les familiers que vous pouvez avoir.', Text0_1_loc2 = 'Eh bien, vous trouverez Shelby Pierre-à-feu à la sortie de la Distillerie Tonnebière, dans Kharanos. Elle s\'occupera bien de tous les familiers que vous pouvez avoir.', Text1_0_loc2 = 'Shelby Pierre-à-feu abrite bon nombre de familiers pour les chasseurs locaux. Vous la trouverez près de la Distillerie Tonnebière, si vous avez besoin qu\'on s\'occupe d\'un de vos compagnons.', Text1_1_loc2 = 'Shelby Pierre-à-feu abrite bon nombre de familiers pour les chasseurs locaux. Vous la trouverez près de la Distillerie Tonnebière, si vous avez besoin qu\'on s\'occupe d\'un de vos compagnons.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5985;
UPDATE locales_npc_text SET entry = 5986, Text0_0_loc2 = 'Prendre soin des animaux est quelque chose qu\'Ulbrek Brasemain fait avec beaucoup de consience professionnelle. Il traîne généralement avec les chasseurs au Hall des Armes, si vous voulez qu\'il s\'occupe de vos familiers.', Text0_1_loc2 = 'Prendre soin des animaux est quelque chose qu\'Ulbrek Brasemain fait avec beaucoup de consience professionnelle. Il traîne généralement avec les chasseurs au Hall des Armes, si vous voulez qu\'il s\'occupe de vos familiers.', Text1_0_loc2 = 'Dans le Hall des Armes, vous trouverez notre maître des écuries local, Ulbrek Brasemain. Il prendra soin de tous les familiers que vous lui confierez.', Text1_1_loc2 = 'Dans le Hall des Armes, vous trouverez notre maître des écuries local, Ulbrek Brasemain. Il prendra soin de tous les familiers que vous lui confierez.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5986;
