UPDATE locales_npc_text SET entry = 6061, Text0_0_loc2 = 'La mine de Gouffrefer est sur le territoire de l\'Alliance, au nord du cimetière Foudrepique.$B$BElle est contrôlée par les troggs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6061;
UPDATE locales_npc_text SET entry = 6062, Text0_0_loc2 = 'Ce monument est dédié à tous ceux qui sont tombés pour la protection de Hurlevent. Notre peuple a payé un lourd tribut pour sauvegarder ses libertés et rester maître de sa destinée. C\'est ici que nous nous souvenons de chaque sacrifice consenti par nos citoyens au cours de la Première Guerre qui a déchiré notre peuple.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6062;
