UPDATE locales_npc_text SET entry = 6153, Text0_0_loc2 = 'Si vous cherchez des informations que le Cercle cénarien pourrait avoir archivées quelque part, c\'est à moi qu\'il faut parler. Mais vous devez savoir quelles informations vous cherchez avant que je ne puisse vous aider.$B$BVous comprenez comment ça marche maintenant ? Parfait !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6153;
