UPDATE locales_npc_text SET entry = 6158, Text0_0_loc2 = '$N, je pense que vous pouvez entreprendre votre tâche ultime. Les runes de feu réparties sur le sol du Cœur du Magma sont des passages vers le royaume du feu et leur présence est à la fois un affront pour nous tous et la source du pouvoir des seigneurs du Feu. Il faut s\'en débarrasser !$B$BPour éteindre une rune, prenez une fiole de quintessence aquatique. Versez-en le contenu sur la rune et sa flamme s\'éteindra, ce qui la rendra inutile et affaiblira nos ennemis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6158;
UPDATE locales_npc_text SET entry = 6159, Text0_0_loc2 = 'Bienvenue au Registre des guildes de Fossoyeuse. Que puis-je pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6159;
