UPDATE locales_npc_text SET entry = 6161, Text0_0_loc2 = 'Parlez à Hydraxis.', Text0_1_loc2 = null, Text1_0_loc2 = 'Je n\'ai rien à voir avec vous.', Text1_1_loc2 = null, Text2_0_loc2 = 'Épargnez-moi votre langage vulgaire. Seul Hydraxis peut le supporter.', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6161;
