UPDATE locales_npc_text SET entry = 6178, Text0_0_loc2 = 'J\'ai été abattu par cet imbécile de Mulverick. J\'ai peur, si je ne retourne pas à la base, que tout soit perdu ! Mulverick et ses Chevaucheurs de guerre doivent être arrêtés !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6178;
