UPDATE locales_npc_text SET entry = 6180, Text0_0_loc2 = 'J\'ai entendu leur rire moqueur tandis que mon griffon s\'écrasait et brûlait, $c.$B$B"Slidore, pauvre larve !" criaient-ils.$B$BPersonne ne parle ainsi à Slidore ! Aidez-moi à sortir de là et je leur montrerai qui est une larve !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6180;
UPDATE locales_npc_text SET entry = 6193, Text0_0_loc2 = null, Text0_1_loc2 = 'Cette forêt peut faire perdre le sens de l\'orientation. Faites attention là-dedans.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6193;
UPDATE locales_npc_text SET entry = 6194, Text0_0_loc2 = 'Rabais pour vous et pour les vôtres !', Text0_1_loc2 = 'Rabais pour vous et pour les vôtres !', Text1_0_loc2 = 'Directement de la ferme à votre assiette : les meilleurs produits sont ceux des Gourmandises Fumebois !', Text1_1_loc2 = 'Directement de la ferme à votre assiette : les meilleurs produits sont ceux des Gourmandises Fumebois !', Text2_0_loc2 = 'C\'est l\'époque des bonnes affaires ! Fah la la la la !', Text2_1_loc2 = 'C\'est l\'époque des bonnes affaires ! Fah la la la la !', Text3_0_loc2 = 'Bonnes fêtes du Voile d\'hiver à tous ! Et pour bien commencer, foncez aux Gourmandises Fumebois pour vous faire plaisir !', Text3_1_loc2 = 'Bonnes fêtes du Voile d\'hiver à tous ! Et pour bien commencer, foncez aux Gourmandises Fumebois pour vous faire plaisir !', Text4_0_loc2 = 'N\'oubliez pas de dire au Grand-père Hiver quel cadeau vous voulez pour le Voile d\'hiver !', Text4_1_loc2 = 'N\'oubliez pas de dire au Grand-père Hiver quel cadeau vous voulez pour le Voile d\'hiver !', Text5_0_loc2 = 'Vive le son des cloches, mais pas les trous dans les poches ! Grâce aux Gourmandises Fumebois, économisez sur vos dépenses pour le Voile d\'hiver !', Text5_1_loc2 = 'Vive le son des cloches, mais pas les trous dans les poches ! Grâce aux Gourmandises Fumebois, économisez sur vos dépenses pour le Voile d\'hiver !', Text6_0_loc2 = 'Si j\'avais des marrons, je les ferais cuire sur les braises en plein air ! Mmmm !', Text6_1_loc2 = 'Si j\'avais des marrons, je les ferais cuire sur les braises en plein air ! Mmmm !', Text7_0_loc2 = 'Ne lésinez pas sur les paquets cadeaux pendant le Voile d\'hiver ! Rien ne vaut un beau nœud rose autour d\'une hache ou d\'une épée pour réjouir le cœur de vos amis !', Text7_1_loc2 = 'Ne lésinez pas sur les paquets cadeaux pendant le Voile d\'hiver ! Rien ne vaut un beau nœud rose autour d\'une hache ou d\'une épée pour réjouir le cœur de vos amis !' WHERE entry = 6194;
