UPDATE locales_npc_text SET entry = 6233, Text0_0_loc2 = 'Vous êtes là pour apprendre à manier une nouvelle arme, ou seulement pour me faire perdre mon temps ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6233;
UPDATE locales_npc_text SET entry = 6235, Text0_0_loc2 = 'Dites-moi quelle arme vous voulez apprendre à manier, ou fichez le camp.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6235;
UPDATE locales_npc_text SET entry = 6250, Text0_0_loc2 = 'Bonnes fêtes du Voile d\'hiver, heu... $Gmon garçon:ma fille;. Les enfants gentils reçoivent des rabais dans toutes les échoppes des Gourmandises Fumebois. Miam !', Text0_1_loc2 = null, Text1_0_loc2 = 'Je crois savoir ce que fera ce vieux Grand-père Hiver quand tout ceci sera fini. J\'ai une bonne bouteille dans mon sac, et j\'ai l\'intention d\'égayer mon Voile d\'hiver en explorant son contenu.', Text1_1_loc2 = null, Text2_0_loc2 = 'Non... Pas question de s\'asseoir sur mes genoux.', Text2_1_loc2 = null, Text3_0_loc2 = 'Ouais, c\'est ça, allez dire au vieux Grand-père Hiver ce que vous voulez comme cadeau pour les fêtes.$B$BAllez, on se grouille !', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6250;
UPDATE locales_npc_text SET entry = 6255, Text0_0_loc2 = 'On dit que le point faible d’une armée est son estomac, et c’est vrai. Pour que nos offensives réussissent, nos troupes doivent être bien nourries et bien ravitaillées.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6255;
UPDATE locales_npc_text SET entry = 6257, Text0_0_loc2 = 'À ce qu’on dit, la force et la bravoure suffisent à assurer le succès d’une armée. C’est vrai ! Mais il faut aussi du matériel pour que l’armée reste en forme, et ma tâche est de veiller à ce que nous ayons tout ce qu’il nous faut.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6257;
