UPDATE locales_npc_text SET entry = 6277, Text0_0_loc2 = 'Quelle arme qu’vous voulez apprendre à manier, $gmon p’tit gars:ma p’tite dame; ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6277;
UPDATE locales_npc_text SET entry = 6279, Text0_0_loc2 = 'Ce sera un honneur pour moi de vous apprendre à utiliser les armes que je maîtrise, si vous avez la capacité d\'apprendre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6279;
UPDATE locales_npc_text SET entry = 6281, Text0_0_loc2 = 'Si vous avez besoin d’un autre apprentissage, je serai heureux de vous indiquer qui pourra vous aider. Quelle arme vous a tapé dans l’œil ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6281;
