UPDATE locales_npc_text SET entry = 6288, Text0_0_loc2 = '$N, c’est bon de voir des vétérans comme vous prendre part à l’action ! Je suis désolé, mais nous n’avons pas assez de matériel pour lancer une offensive terrestre importante contre la Horde.$B$BDiscutez-en avec notre intendant, il a des missions de ravitaillement à vous confier.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6288;
