UPDATE locales_npc_text SET entry = 6291, Text0_0_loc2 = null, Text0_1_loc2 = 'La grâce avec laquelle je me bats n\'est pas quelque chose que je peux enseigner, mais je peux vous apprendre à utiliser différentes armes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6291;
UPDATE locales_npc_text SET entry = 6292, Text0_0_loc2 = null, Text0_1_loc2 = 'Je peux vous indiquer d\'autres maîtres d\'armes, si vous préférez. Qu\'est-ce qui vous intéresse ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6292;
UPDATE locales_npc_text SET entry = 6293, Text0_0_loc2 = 'Vous voulez apprendre l\'utilisation d\'une arme, n\'est-ce pas ? Ne restez pas là comme une cruche, parlez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6293;
UPDATE locales_npc_text SET entry = 6294, Text0_0_loc2 = 'Quelle arme qu\'ça vous dirait d\'apprendre ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6294;
UPDATE locales_npc_text SET entry = 6295, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut ! Vous êtes là pour apprendre à utiliser correctement une nouvelle arme ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6295;
UPDATE locales_npc_text SET entry = 6296, Text0_0_loc2 = null, Text0_1_loc2 = 'Oooh, vous ne voulez pas vous entraîner avec moi ? Quelles sont les autres armes qui vous ont tapé dans l’œil ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6296;
UPDATE locales_npc_text SET entry = 6313, Text0_0_loc2 = 'Mort aux Loup-de-givre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6313;
