UPDATE locales_npc_text SET entry = 6316, Text0_0_loc2 = null, Text0_1_loc2 = 'Les béliers indigènes d\'Alterac sont vitaux pour le fonctionnement de notre cavalerie !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6316;
UPDATE locales_npc_text SET entry = 6317, Text0_0_loc2 = null, Text0_1_loc2 = 'Encore un effort et nos écuries déborderont de montures. Laissez-le sous ma garde, soldat. Je m\'en charge.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6317;
UPDATE locales_npc_text SET entry = 6333, Text0_0_loc2 = 'Heureux de vous rencontrer, $N. Soyons brefs, il y a beaucoup à faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6333;
UPDATE locales_npc_text SET entry = 6334, Text0_0_loc2 = 'Que Cénarius vous guide, $c.$B$BJe suis ici avec mes sœurs, dans ces terres corrompues, chargé d\'une mission de grande importance pour le Cercle cénarien. Mais seuls ceux qui sont assez puissants doivent me parler de ces choses. Envoyer un être inférieur serait pure folie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6334;
UPDATE locales_npc_text SET entry = 6335, Text0_0_loc2 = null, Text0_1_loc2 = 'Mes sœurs et moi sommes venues des Serres-Rocheuses pour mettre fin à une très ancienne tragédie. Malheureusement, nous n\'avons pas la puissance requise pour accomplir nos objectifs par nous-mêmes et nous cherchons des mortels tels que vous pour nous aider.$B$BSi vous disposez de la puissance voulue, $c, nous devons discuter. Dans le cas contraire, pour votre sécurité, je ne mentionnerai pas les détails de notre mission.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6335;
UPDATE locales_npc_text SET entry = 6336, Text0_0_loc2 = 'Bienvenue à vous, $r.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6336;
UPDATE locales_npc_text SET entry = 6353, Text0_0_loc2 = 'Je n\'ai pas besoin de foyer pour savoir quelle place j\'ai toujours dans ce monde, $c. Les tribus verront... elles verront toutes. Elles se déchirent les unes les autres, et pour quoi ? Des restes jetés aux vautours... Des terres infestées par la Légion... bah !$B$BLes centaures devraient être fiers de leur héritage, il vaut bien celui des elfes de la nuit ou des orcs. Il est temps que nous commencions à agir en conséquence.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6353;
UPDATE locales_npc_text SET entry = 6354, Text0_0_loc2 = 'Au moins, je suis libre de la corruption qui me liait naguère à cet endroit...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6354;
UPDATE locales_npc_text SET entry = 6373, Text0_0_loc2 = 'Merci de ce que vous avez fait, $N.$B$BGardez le sceptre en sûreté, et qu\'il vous aide dans votre combat contre les maléfices qui ont envahi ces lieux...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6373;
UPDATE locales_npc_text SET entry = 6375, Text0_0_loc2 = 'Mon sceptre... Après toutes ces années... Vous l\'avez retrouvé ! Vous serez véritablement le sauveur de ces lieux !$B$BÉcoutez-moi attentivement, $N. La tête et le bâton du sceptre ne peuvent être réunis qu\'à la Pierre de Maraudon.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6375;
