UPDATE locales_npc_text SET entry = 6379, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6379;
UPDATE locales_npc_text SET entry = 6380, Text0_0_loc2 = 'Il y a longtemps, mes sœurs dryades et moi sommes entrés dans ces grottes pour libérer l\'esprit torturé de mon oncle, Zaetar. Mais la corruption s\'était déjà répandue dans ces grottes et nous a submergés, nous sommes tombés aussi.$B$BNous étions destinés à errer dans ces couloirs, les toxines empoisonnant nos esprits...$B$BMon esprit est désormais au repos, mais je sais que j\'ai échoué dans ma mission. C\'est une grande douleur avec laquelle je dois vivre...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6380;
UPDATE locales_npc_text SET entry = 6381, Text0_0_loc2 = 'Ah, Zaetar... <Celebras détourne le regard.>$B$BMon père, Remulos, a tenté de l\'arrêter...$B$BZaetar était têtu. Quand il est tombé amoureux d\'une princesse des élémentaires de terre, mon père savait que leur union était impossible, mais Zaetar a refusé de l\'écouter.$B$BLes centaures sont le résultat de son erreur. Ils sont nés difformes et furieux. Mon oncle Zaetar a été tué par sa propre descendance...$B$BLa princesse maléfique, Theradras, garde toujours ses restes dans les Cavernes de cristal.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6381;
UPDATE locales_npc_text SET entry = 6383, Text0_0_loc2 = 'Bienvenue à la Ligue des explorateurs. Si vous avez besoin d\'informations, et qu\'elles se trouvent dans un livre, je serai peut-être en mesure de vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6383;
UPDATE locales_npc_text SET entry = 6384, Text0_0_loc2 = 'Tandis que les toxines infestant Maraudon s\'introduisaient dans mon esprit, je commençai à perdre le sens des réalités. Craignant que cela ne serve au mal, mes sœurs dryades me prirent en secret le sceptre et le brisèrent en deux pour le cacher dans les cavernes.$B$BElles devinrent vite la proie de la folie elles aussi et les deux morceaux du sceptre, sans garde, tombèrent en la possession de Vylelangue en personne. Le satyre en confia une partie à Noxcion, un élémentaire maléfique, et il détient toujours l\'autre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6384;
UPDATE locales_npc_text SET entry = 6385, Text0_0_loc2 = 'Ignorant les incantations requises pour unir les deux morceaux, le seigneur Vylelangue était incapable d\'utiliser le sceptre. Mais maintenant que je suis libre de la corruption de Vylelangue, je vais pouvoir vous aider, à condition que vous me rapportiez les deux morceaux : la tête et le bâton.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6385;
UPDATE locales_npc_text SET entry = 6393, Text0_0_loc2 = 'Vous voulez parler des fils de Cénarius, Remulos et Zaetar.$B$BMon père, Remulos, était fort et séduisant et mon oncle, Zaetar, malin et de constitution fluette. Ils étaient respectés à l\'égal l\'un de l\'autre, mais Zaetar eut toujours le sentiment qu\'il ne pouvait se comparer à la gloire de Remulos et à l\'attention qu\'il semblait recevoir.$B$BAprès ma naissance et celle de mes sœurs, Zaetar, aveuglé par la jalousie, décida de contrer son frère... en devenant créateur lui aussi. Le temps est incapable d\'atténuer la douleur qu\'il nous a causée...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6393;
UPDATE locales_npc_text SET entry = 6413, Text0_0_loc2 = 'Salutations, $N. Je suis le docteur Gregory Victor, chirurgien-chef de l\'unité médicale de la Horde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6413;
UPDATE locales_npc_text SET entry = 6414, Text0_0_loc2 = 'Quel plaisir de vous revoir, docteur $N. J\'ai suivi vos progrès et je dois dire que vous êtes désormais en mesure d\'apprendre des techniques de bandage plus avancées.$B$BRevenez me voir de temps en temps, au fur et à mesure que vos compétences s\'étofferont, je vous apprendrai tout ce que je sais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6414;
UPDATE locales_npc_text SET entry = 6415, Text0_0_loc2 = 'Salutations, $N. Je suis le docteur Gustaf VanHowzen, chirurgien-chef de l\'unité médicale de l\'Alliance.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6415;
