UPDATE locales_npc_text SET entry = 6437, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6437;
UPDATE locales_npc_text SET entry = 6475, Text0_0_loc2 = 'Je m\'occupe du recrutement pour le clan Loup-de-givre. En tant que soldat des Loups-de-givre, vous aurez le devoir de venir me trouver lorsque vous progresserez dans les rangs du clan.$B$BHonneur et respect vous attendent sur ce champ gelé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6475;
UPDATE locales_npc_text SET entry = 6476, Text0_0_loc2 = 'Bonjour, $r ! Je suis le lieutenant Haggerdin, l\'officier responsable du recrutement de la garde Foudrepique.$B$BRevenez me voir lorsque vous gagnerez du prestige et de la notoriété parmi les Foudrepiques. Je m\'occuperai de mettre à jour votre insigne. Si vous perdez votre insigne, je pourrai le remplacer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6476;
