UPDATE locales_npc_text SET entry = 6513, Text0_0_loc2 = 'Nos ancêtres nous enseignent que l\'individu doit être fort, mais que le nombre apporte la sécurité. Voulez-vous défendre l\'honneur des Pitons du Tonnerre en organisant une guilde ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6513;
