UPDATE locales_npc_text SET entry = 6535, Text0_0_loc2 = 'Une grande chasse attend maître Nesingwary. Je trouve que le préparer pour une journée dans la jungle est beaucoup moins stressant que de l\'aider à se préparer au combat. Malgré tout, ces moments glorieux sur le champ de bataille me manquent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6535;
UPDATE locales_npc_text SET entry = 6553, Text0_0_loc2 = null, Text0_1_loc2 = 'Je connais ces forêts comme si elles faisaient partie de moi… Mais il reste beaucoup à en apprendre, et elles renferment nombre de dangers cachés.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6553;
UPDATE locales_npc_text SET entry = 6555, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous avez terminé la mission de Teronis, et j\'apprécie ce que vous avez fait, $N.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6555;
UPDATE locales_npc_text SET entry = 6556, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut ! Non, pour sûr, cet atelier n\'est pas le mien ! Mais dès que j\'aurai fini ma formation, je deviendrai inventeur, comme mon héros, Marvon Chercherivet.$B$BVous n\'avez jamais entendu parler de lui ? Il est parti il y a quelque temps pour étudier d\'anciennes ruines trolles dont il parlait souvent. Il a pris un zeppelin... Je ne sais pas au juste où il est allé...$B$BTanarasin ? Tenkeris ? Taneranisany ?$B$B<Liv secoue la tête>$B$BNon, ce n\'est pas ça...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6556;
