UPDATE locales_npc_text SET entry = 6573, Text0_0_loc2 = 'Vous avez appris tout ce que je peux vous enseigner pour l\'instant, docteur $N. Revenez me voir lorsque vous aurez un peu de pratique, et je vous apprendrai des facettes plus avancées de l\'art médical.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6573;
UPDATE locales_npc_text SET entry = 6574, Text0_0_loc2 = null, Text0_1_loc2 = 'Cet endroit… est maudit.$B$BJ\'ai réussi à m\'échapper de justesse, mais je ne supporte pas l\'idée de redescendre dans ces tunnels.$B$BMon frère et mes sœurs n\'ont pas eu ma chance… Ils sont piégés à l\'intérieur. Les miasmes corrompus qui émanent de ce jardin maléfique leur ont donné la fièvre.$B$BVous devez nous aider. S\'il vous plaît.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6574;
