UPDATE locales_npc_text SET entry = 6795, Text0_0_loc2 = 'S\'il vous plaît, dites-moi que vous êtes là pour me libérer de ces menottes… Il doit bien y avoir une clé quelque part… $B$BVous pouvez peut-être au moins liquider le garde Slip\'kik. Je pense que je pourrais vous aider à sortir !$B$BVous voyez ce piège cassé, là-bas ? Les ogres sont trop bêtes pour le réparer. Il a besoin d\'un rouage en thorium et d\'un peu d\'huile glaciale. Si vous le réparez, vous pourrez y attirer Slip\'kik et CRAC ! Un garde ogre piégé !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6795;
UPDATE locales_npc_text SET entry = 6796, Text0_0_loc2 = 'Il y a deux mines dans la vallée : celle de Gouffrefer sur le territoire de l\'Alliance, et celle de Froide-dent sur le territoire de la Horde. Toutes deux sont riches en minerais. De plus, autrefois, elles ont été utilisées pour conserver du matériel.$B$BÀ présent, la Horde, l\'Alliance et les habitants du coin se les disputent férocement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6796;
UPDATE locales_npc_text SET entry = 6797, Text0_0_loc2 = 'Les tours et les fortins de la vallée d\'Alterac ont une grande valeur stratégique. Si nous contrôlons une tour, nos troupes peuvent s\'en servir pour tirer à de grandes distances.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6797;
UPDATE locales_npc_text SET entry = 6798, Text0_0_loc2 = 'Les cimetières de la vallée d\'Alterac sont l\'endroit où les âmes des défunts trouvent répit et secours. Si vous mourez, votre esprit dérive jusqu\'au plus proche cimetière contrôlé par notre camp.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6798;
