UPDATE locales_npc_text SET entry = 6842, Text0_0_loc2 = 'Je sais bien des choses. Écoutez et faites votre éducation, $R.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6842;
UPDATE locales_npc_text SET entry = 6843, Text0_0_loc2 = 'Dans un temps d’avant le temps, que les usurpateurs appelleront plus tard le Premier Âge de la Création, mon maître et les siens régnaient sur ce monde. Ce sont eux, les Dieux très anciens, qui ont tout créé.$B$BEt ce sont eux qui, plus tard, ont été rejetés par ceux qui osent se parer du nom de Formeurs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6843;
UPDATE locales_npc_text SET entry = 6844, Text0_0_loc2 = 'Leurs formes visibles étaient affaiblies par des éternités de combats. Ils étaient emprisonnés sous le monde, ce monde qu’ils avaient eux-mêmes créé. La corruption des Titans Formeurs a pénétré sous la surface et souillé les Dieux très anciens.$B$BC’est alors, en ce lieu, que les frères se sont retournés les uns contre les autres. Pendant cinq mille ans, une bataille a fait rage au cœur du monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6844;
