UPDATE locales_npc_text SET entry = 6848, Text0_0_loc2 = 'Les capitaines de la Horde et de l\'Alliance sont montés en ligne. Notre capitaine Galvangar dirige les Loups-de-givre depuis la garnison de Glace-sang. Son rival, le capitaine Balinda Stoneheart, se terre dans l\'avant-poste de Stonehearth.$B$BLa bataille est commencée !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6848;
