UPDATE locales_npc_text SET entry = 6853, Text0_0_loc2 = 'La vallée d\'Alterac appartient au clan Loup-de-givre depuis des années, et nous ne laisserons pas les nains et leur Alliance s\'y installer ! C\'est pour cela que nous nous battons !$B$BPour reprendre Alterac, il faut vaincre leur général Vanndar Foudrepique à Dun Baldar.$B$BPour la Horde !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6853;
