UPDATE locales_npc_text SET entry = 6859, Text0_0_loc2 = 'Le capitaine Balinda Stonehearth commande les forces de l\'Alliance qui se trouvent en première ligne. Elle est basée à l\'avant-poste de Stonehearth, dans la partie nord du Champ sanglant.$B$BBalinda se trouve actuellement à l\'avant-poste de Stonehearth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6859;
UPDATE locales_npc_text SET entry = 6861, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6861;
UPDATE locales_npc_text SET entry = 6863, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6863;
UPDATE locales_npc_text SET entry = 6867, Text0_0_loc2 = 'C’est durant la Fracture élémentaire que le seigneur du Feu, Ragnaros, a tenté de consumer mon seigneur, Tonneraan, prince de l’Air.$B$B<Demitrian s’incline, son visage tordu par la rage.>$B$BPrononcez son nom et je décorerai ce camp ravagé avec vos entrailles. Restez silencieux et écoutez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6867;
UPDATE locales_npc_text SET entry = 6868, Text0_0_loc2 = 'Ce sont les lieutenants de Ragnaros, Geddon et Garr, qui ont été les instruments de la trahison. Tonneraan ne se doutait de rien.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6868;
UPDATE locales_npc_text SET entry = 6869, Text0_0_loc2 = 'L’avancée du seigneur du Feu était passée inaperçue de Tonneraan. Avant que mon seigneur ne puisse réagir, Sulfuras, le marteau légendaire de Ragnaros, a frappé un coup fatal. Tonneraan est tombé, à jamais vaincu.$B$BLe seigneur du Feu s’est repu de l’essence de mon seigneur, mais il n’est pas parvenu à le dévorer entièrement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6869;
UPDATE locales_npc_text SET entry = 6870, Text0_0_loc2 = 'Le peu qui restait de l’essence de Tonneraan fut conservé dans un talisman de lien élémentaire. Ragnaros a brisé ce talisman en deux morceaux parfaits, qui furent donnés à ses lieutenants…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6870;
UPDATE locales_npc_text SET entry = 6871, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6871;
UPDATE locales_npc_text SET entry = 6875, Text0_0_loc2 = 'Vous avez trouvé une clé ? Bien, mais est-ce LA clé ? Je veux dire, celle qui ouvre ces menottes ?$B$BEh bien, $gmessire:madame;, il n\'y a qu\'une manière de le savoir !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6875;
UPDATE locales_npc_text SET entry = 6876, Text0_0_loc2 = 'Vous grand patron maintenant ! Vous roi ! Vive le roi $N, roi des Gordok !$B$BMaintenant qu\'v-z-êtes le patron, tous les sbires vous doivent tribut ! Vous en laissez certains en vie, hein patron ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6876;
UPDATE locales_npc_text SET entry = 6877, Text0_0_loc2 = 'Tu veux jouer à un petit jeu, puant ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6877;
UPDATE locales_npc_text SET entry = 6878, Text0_0_loc2 = 'Je détiens la clé qui ouvre vers la liberté ! Pourchassez-moi et vous le verrez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6878;
UPDATE locales_npc_text SET entry = 6879, Text0_0_loc2 = 'Toujours en vie ? J\'en suis surpris. Regardez comme je fuis !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6879;
UPDATE locales_npc_text SET entry = 6880, Text0_0_loc2 = 'Cessez de grimacer, souriez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6880;
UPDATE locales_npc_text SET entry = 6881, Text0_0_loc2 = 'C\'est le bout de la route, nuisible qui me dégoûte !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6881;
UPDATE locales_npc_text SET entry = 6882, Text0_0_loc2 = 'Ben, vous avez tué l\'roi, pas vrai ? Ça veut dire qu\'vous prenez sa place. Ça fait d\'vous l\'nouveau patron ! Vive le roi !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6882;
UPDATE locales_npc_text SET entry = 6883, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6883;
UPDATE locales_npc_text SET entry = 6894, Text0_0_loc2 = 'Z\'êtes l\'patron maintenant, pas vrai ? Vive l\'nouveau roi !$B$BKreeg vous laisser regarder la gnôle des Gordok. Gnôle ogre, meilleure gnôle de toutes les gnôles, et gnôle des Gordok, meilleure gnôle de toutes les gnôles ogres ! Et p\'têt\' que vous allez m\'en acheter, hein patron ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6894;
UPDATE locales_npc_text SET entry = 6895, Text0_0_loc2 = 'OK patron. Quand vous s\'rez prêt pour l\'tribut des sbires, vous m\'le dites !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6895;
UPDATE locales_npc_text SET entry = 6896, Text0_0_loc2 = null, Text0_1_loc2 = 'Quittez cet endroit, $gétranger:étrangère;, de peur qu’il ne vous dévore comme il a dévoré tout ce qu’il a touché.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6896;
UPDATE locales_npc_text SET entry = 6897, Text0_0_loc2 = null, Text0_1_loc2 = 'Que savez-vous des Bien-nés ? Des Shen\'dralar ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6897;
UPDATE locales_npc_text SET entry = 6898, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Shen’dralar étaient les arcanistes les plus révérés de la reine Azshara. C’est dans ces murs qu’étaient traitées les requêtes les plus importantes de la reine, souvent en secret.$B$BAprès la destruction du Puits, une terrible léthargie s’est abattue sur les Bien-nés et sur les sectes inférieures des elfes de la nuit. C’est alors que le prince d’Eldre’Thalas, Tortheldrin, a commencé la construction des pylônes dans les salles de l’aile ouest.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6898;
UPDATE locales_npc_text SET entry = 6899, Text0_0_loc2 = null, Text0_1_loc2 = 'Le prince tenta d’emprisonner un démon très puissant derrière un vaste champ de force. Les pylônes devaient canaliser les énergies latentes vers le champ, le maintenant en activité.$B$BEt durant son emprisonnement, Immol’thar devait servir de batterie à la population d’Eldre’Thalas.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6899;
UPDATE locales_npc_text SET entry = 6900, Text0_0_loc2 = null, Text0_1_loc2 = 'Les énergies ainsi absorbées devaient soutenir la magie des Bien-nés, permettant à ceux qui vivaient entre les murs d’Eldre’Thalas d’alimenter leurs besoins croissants en magie.$B$BDes milliers d’années passèrent. L’équilibre changea. L’énergie nécessaire pour maintenir Immol’thar en prison ne cessa de grandir, jusqu’à dépasser celle qu’il fallait pour préserver la vie à l’intérieur d’Eldre‘Thalas. Bientôt, la prison d’Immol’thar consomma plus d’énergie qu’il n’était possible d’en absorber et d’en répartir. Ce qui était un gain net devint une perte.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6900;
UPDATE locales_npc_text SET entry = 6901, Text0_0_loc2 = null, Text0_1_loc2 = 'Les fantômes de bien-nés que vous voyez dans la cour et les salles d’Eldre’Thalas ont été tués, non par les envahisseurs, ogres gordok ou satyres, mais par le prince en personne !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6901;
UPDATE locales_npc_text SET entry = 6902, Text0_0_loc2 = null, Text0_1_loc2 = 'Le peuple fut anéanti pour préserver l’immortalité et les pouvoirs quasi-illimités du prince. Autrefois divisé entre des milliers d’individus, le pouvoir est à présent dirigé vers le prince et ses zélotes Shen’dralar. Les quelques survivants alimentent directement les pylônes pour qu’Immol’thar reste emprisonné.$B$BSi le démon se libérait, il détruirait certainement le peu qu’il reste de ce bastion des arcanes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6902;
UPDATE locales_npc_text SET entry = 6903, Text0_0_loc2 = 'T\'es l\'chef maintenant, chien !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6903;
UPDATE locales_npc_text SET entry = 6904, Text0_0_loc2 = 'Fengus vous rend féroce ! Fou ! Rarrr !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6904;
UPDATE locales_npc_text SET entry = 6905, Text0_0_loc2 = 'Tu m\'as bien eu, patron ! Comment t\'as fait pour arriver près Skip\'kik ? Mais moi malin, moi faire gaffe la prochaine fois ! Har har har !$B$BPersonne arrive plus près Slip\'kik jamais ! Silp\'kik promet !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6905;
UPDATE locales_npc_text SET entry = 6906, Text0_0_loc2 = 'J\'vais vous montrer comme ch\'suis malin, patron ! Avec l\'grand mojo magique, vous serez super-malin aussi !$B$BEuh… p\'têt Slip\'kik utiliser magie sur lui. Bonne idée, ça…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6906;
UPDATE locales_npc_text SET entry = 6907, Text0_0_loc2 = 'Z\'êtes le roi des Gordok ! Z-êtes l\'Numéro-Un !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6907;
UPDATE locales_npc_text SET entry = 6908, Text0_0_loc2 = 'J\'avais entendu que vous étiez mort… bon, après ça, vous serez plus dur à faire mort !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6908;
UPDATE locales_npc_text SET entry = 6909, Text0_0_loc2 = 'J\'ai gouverné ce royaume pendant dix mille ans. J\'ai entendu bien des récits fascinants de la part de ceux qui cherchent à pénétrer dans la demeure sacrée de la Maison de Shen\'dralar.$B$BÉtant lié à cet endroit, j\'échange souvent des histoires avec les visiteurs. Avez-vous une histoire pour moi ? À moins que vous ne souhaitiez entendre une histoire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6909;
UPDATE locales_npc_text SET entry = 6911, Text0_0_loc2 = 'Vous ne devez pas fâcher le Maître. Il est affreusement cruel quand il est en colère.', Text0_1_loc2 = 'Vous ne devez pas fâcher le Maître. Il est affreusement cruel quand il est en colère.', Text1_0_loc2 = 'Parlez bas, $g étranger : étrangère ;. Il ne faut pas déranger le Maître dans ses méditations.', Text1_1_loc2 = 'Parlez bas, $g étranger : étrangère ;. Il ne faut pas déranger le Maître dans ses méditations.', Text2_0_loc2 = 'Bien des gens sont entrés dans ce sanctuaire. Peu en sont sortis.', Text2_1_loc2 = 'Bien des gens sont entrés dans ce sanctuaire. Peu en sont sortis.', Text3_0_loc2 = 'Le Gardien du savoir est très âgé. Il a servi sous notre glorieuse reine !', Text3_1_loc2 = 'Le Gardien du savoir est très âgé. Il a servi sous notre glorieuse reine !', Text4_0_loc2 = 'Ces derniers temps, les livres disparaissent. Le Gardien du savoir et ses assistants sont sur les dents. Ils soupçonnent que le coup vient d\'Eldre\'Thalas.', Text4_1_loc2 = 'Ces derniers temps, les livres disparaissent. Le Gardien du savoir et ses assistants sont sur les dents. Ils soupçonnent que le coup vient d\'Eldre\'Thalas.', Text5_0_loc2 = 'Le prince vous a-t-il raconté l\'histoire de Porte-cendres ? C\'est notre préférée. Sa préférée est celle qui vient après, mais personne n\'a jamais réussi à le pousser à la raconter.', Text5_1_loc2 = 'Le prince vous a-t-il raconté l\'histoire de Porte-cendres ? C\'est notre préférée. Sa préférée est celle qui vient après, mais personne n\'a jamais réussi à le pousser à la raconter.', Text6_0_loc2 = 'Je soupçonne les diablotins de voler du savoir.', Text6_1_loc2 = 'Je soupçonne les diablotins de voler du savoir.', Text7_0_loc2 = 'Le Maître s\'intéresse beaucoup au repaire de Nefarian. Si quelqu\'un pouvait vaincre le dragon et ses séides et raconter l\'histoire au prince, je suis sûr que cela le pousserait à parler. Porte-cendres pourrait être trouvée !', Text7_1_loc2 = 'Le Maître s\'intéresse beaucoup au repaire de Nefarian. Si quelqu\'un pouvait vaincre le dragon et ses séides et raconter l\'histoire au prince, je suis sûre que cela le pousserait à parler. Porte-cendres pourrait être trouvée !' WHERE entry = 6911;
UPDATE locales_npc_text SET entry = 6913, Text0_0_loc2 = 'Qu\'est-ce que vous faites là ? Pourquoi ces p\'tits poissons sont pas enchaînés ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6913;
UPDATE locales_npc_text SET entry = 6914, Text0_0_loc2 = 'Z\'êtes l\'grand costaud maintenant ! Tous saluent l\'nouveau roi des Gordok !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6914;
UPDATE locales_npc_text SET entry = 6915, Text0_0_loc2 = 'Ils ont encore leurs armes ? Pourquoi tu les laisses garder leurs armes ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6915;
UPDATE locales_npc_text SET entry = 6916, Text0_0_loc2 = 'Tous les sbires du patron – Kreeg le Marteleur, les trois de la Garde, l\'capitaine Kromcrush et l\'Observateur – y donnent un tribut au n\'veau roi ! Si vous tuez un sbire, y peut plus payer le tribut. Mais chaque sbire qui reste en vie y vous rapporte plus de tribut. Si vous les gardez tous, v-z-aurez un GROS tribut ! Ouais !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6916;
UPDATE locales_npc_text SET entry = 6917, Text0_0_loc2 = null, Text0_1_loc2 = 'Quelle horreur ! Quel drame ! Nous étions sur le point de faire une percée, mais nous risquons de tout perdre !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6917;
UPDATE locales_npc_text SET entry = 6918, Text0_0_loc2 = 'Heu… désolé d\'vous avoir attaqué, patron. Vous m\'tuez pas, d\'ac ? Z\'êtes le roi maintenant, et j\'vous aiderai à avoir un plus gros tribut !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6918;
UPDATE locales_npc_text SET entry = 6919, Text0_0_loc2 = 'Un examen rapide du squelette indique que la chair a été calcinée. C\'est un spectacle bien sinistre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6919;
UPDATE locales_npc_text SET entry = 6920, Text0_0_loc2 = 'P\'têt\' ben que vous serez mieux que l\'ancien roi pour reprendre la précieuse relique gordok au sale prince Bien-né !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6920;
UPDATE locales_npc_text SET entry = 6922, Text0_0_loc2 = 'Vive l\'nouveau roi !', Text0_1_loc2 = null, Text1_0_loc2 = 'Quand j’vous ai r’gardé de travers avant qu\'vous dev’niez l’nouveau roi. Euh… j’blaguais.$B$BOuais !', Text1_1_loc2 = null, Text2_0_loc2 = 'Les autres ogres étaient crétins d’vous attaquer, patron ! C’est pour ça que j’suis toujours en vie : j’suis malin !', Text2_1_loc2 = null, Text3_0_loc2 = 'On est beaucoup plus propres qu’les ogres de Tanaris. Dans le désert, y a du caca d’ogre à perte de vue. Pas ici, non non non !', Text3_1_loc2 = null, Text4_0_loc2 = 'Tant qu\'vous restez avec nous, z’êtes l’roi. Vous partez, un autre ogre prend vot’place. Ça s\'passe comme ça chez les Gordok !', Text4_1_loc2 = null, Text5_0_loc2 = 'Ch\'suis malin ! M-A-L-L-U-N !', Text5_1_loc2 = null, Text6_0_loc2 = 'L’vieux roi est mort ? C’est pas possible !', Text6_1_loc2 = null, Text7_0_loc2 = 'J\'f\'rai pas de problèmes, patron.', Text7_1_loc2 = null WHERE entry = 6922;
UPDATE locales_npc_text SET entry = 6923, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6923;
UPDATE locales_npc_text SET entry = 6924, Text0_0_loc2 = 'Alors, vous avez tué le roi, hein ? Je savais que ces clowns vous mettraient sur le trône. Je le savais ! C\'est tout ce que ces abrutis comprennent – la force.$B$BÀ ce que j\'ai compris, plus vous garderez de sbires en vie, plus votre tribut sera élevé. Pas mal – à mon avis, c\'est un défi intéressant. Si vous partez, vous ne resterez pas roi. L\'un de ces ploucs prendra votre place.$B$BOh, une dernière chose… vous n\'avez pas trouvé ma clé, par hasard ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6924;
UPDATE locales_npc_text SET entry = 6925, Text0_0_loc2 = 'Vous devez comprendre que la possibilité de dresser l\'index de dix mille ans d\'existence m\'a donné une perspective unique. Votre époque et vos tribulations n\'ont pas de sens.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6925;
UPDATE locales_npc_text SET entry = 6926, Text0_0_loc2 = 'Le jeune Maître a fait tout un drame. Vous auriez cru que le sort de l\'univers reposait sur ses épaules. Il faut être mortel pour avoir une vision du monde aussi faussée.$B$BHélas, il est parti avec ses maigres secrets magiques… Des libram ou quelque chose de ce genre. Ses enchantements ridicules ne me serviraient à rien, mais vous pourriez leur trouver un intérêt…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6926;
UPDATE locales_npc_text SET entry = 6927, Text0_0_loc2 = 'Ils ont été volés, $R ! Trois de nos livres les plus précieux. Si vous nous les retrouvez, vous serez certainement $grécompensé:récompensée;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6927;
UPDATE locales_npc_text SET entry = 6929, Text0_0_loc2 = '<Vous ne comprenez rien à tous les trucs-machins techniques marqués sur ce parchemin.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6929;
UPDATE locales_npc_text SET entry = 6930, Text0_0_loc2 = '<Ce parchemin détaille le schéma de fabrication d’un robot réparateur.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6930;
UPDATE locales_npc_text SET entry = 6931, Text0_0_loc2 = '<Vos maigres talents d\'ingénieur ne sont pas à la hauteur pour déchiffrer ce schéma compliqué.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6931;
UPDATE locales_npc_text SET entry = 6932, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6932;
UPDATE locales_npc_text SET entry = 6933, Text0_0_loc2 = 'Le tissu qui sous-tend notre monde est très fragile. Pour le déchirer, il suffit de maîtriser quelques connaissances et de savoir les appliquer. C’est ainsi que naissent les failles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6933;
UPDATE locales_npc_text SET entry = 6934, Text0_0_loc2 = 'Contemplez l’ennemi, $r. Voyez à quoi la puissance de la Horde a réduit le plus puissant des dragons ! Tous ceux qui s’opposent à nous connaîtront le même destin !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6934;
UPDATE locales_npc_text SET entry = 6935, Text0_0_loc2 = 'Je suis celui qui porte les ordres du Chef de guerre. Je suis Horde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6935;
UPDATE locales_npc_text SET entry = 6936, Text0_0_loc2 = 'Je ne vis que pour servir notre roi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6936;
UPDATE locales_npc_text SET entry = 6937, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6937;
UPDATE locales_npc_text SET entry = 6938, Text0_0_loc2 = 'Votre présence est un honneur, monseigneur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6938;
UPDATE locales_npc_text SET entry = 6939, Text0_0_loc2 = 'La présence de dix mille ans de savoir accumulé a tendance à attirer des individus peu fréquentables à l\'Athenaeum. Tous les jours, des livres disparaissent, sont retrouvés et sont volés à nouveau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6939;
UPDATE locales_npc_text SET entry = 6940, Text0_0_loc2 = null, Text0_1_loc2 = 'Kildrath a tendance à dramatiser. Par exemple : “Garona : une étude en discrétion et en trahison” a été volé plus de quatre cents fois.$B$BLa marque de l’Athenaeum protège les livres et empêche les consultations non-autorisées.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6940;
UPDATE locales_npc_text SET entry = 6947, Text0_0_loc2 = 'Ne m’interrompez pas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6947;
UPDATE locales_npc_text SET entry = 6948, Text0_0_loc2 = 'Puis-je vous aider ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6948;
UPDATE locales_npc_text SET entry = 6949, Text0_0_loc2 = 'Oui, mais je le connais sous son véritable nom, Eldre’Thalas. C’était un bastion de la magie des elfes de la nuit il y a maintes et maintes années.$B$BVous avez entendu parler d’Eldre’Thalas, mon enfant ? Compte tenu de mon problème présent, on pourrait croire à une coïncidence… mais je ne crois pas aux coïncidences.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6949;
UPDATE locales_npc_text SET entry = 6950, Text0_0_loc2 = 'Les agents du Cercle cénarien ont découvert des informations inquiétantes à propos de la situation à Eldre’Thalas. Je suis dans une impasse, ne sachant pas quoi faire à ce sujet, mais je vais prochainement chercher l’aide d’individus capables de se battre.$B$BToutefois, pour agir de manière intelligente à Eldre’Thalas, il est important de connaître l’histoire d’un objet, le Fruit de Fertilité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6950;
UPDATE locales_npc_text SET entry = 6951, Text0_0_loc2 = 'Au temps jadis, Eldre’Thalas était un nexus magique, et ceux qui vivaient dans ses salles sacrées contrôlaient un pouvoir immense… Parmi eux se trouvait une elfe de la nuit nommée Milicent Serene, une naturaliste qui introduisait le pouvoir de la nature lui-même dans les puissants objets magiques qu’elle forgeait.$B$BSon chef-d’œuvre fut le Fruit de la Fertilité… elle se servit d’un puits de lune spécialement préparé pour faire pousser une vigne exotique qu’elle dota d’une aura de croissance et de protection. Tout Eldre’Thalas profita de son existence, et la ville fut plus florissante que jamais.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6951;
UPDATE locales_npc_text SET entry = 6952, Text0_0_loc2 = 'Comme toujours, hélas. Comme tout le reste du Cercle cénarien, j’avais supposé que la création de Milli avait été détruite lors de la Guerre des Anciens. Mais j’ai fini par apprendre que j’étais dans l’erreur.$B$BLe Fruit de Fertilité a survécu… mais maintenant, on l’appelle la Gangrevigne. Elle a été corrompue par le seigneur démoniaque de cette partie d’Eldre’Thalas, Alzzin le Modeleur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6952;
UPDATE locales_npc_text SET entry = 6953, Text0_0_loc2 = 'Je… je ne peux même pas commencer à l’imaginer, $N. Tout ce que je sais, c’est qu’il n’est pas question que cela se produise. Elle doit être détruite, à la fois pour nous protéger de la corruption démoniaque et pour honorer la mémoire de Millicent Serene… Milli…$B$BEt c’est là, mon $gami:amie;, que j’espère que vous allez intervenir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 6953;
