UPDATE locales_npc_text SET entry = 7013, Text0_0_loc2 = 'Je vous salue, $gjeune homme:jeune fille;, et vous souhaite la bienvenue aux Pitons du Tonnerre. Vous y trouverez un abri contre les fardeaux de ce monde. Tous les membres de la Horde y sont les bienvenus.$B$BLes esprits du présent et du passé prennent vie en ce lieu sacré, mon $gami:amie;. Honorez-les, car ainsi, vous vous honorerez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7013;
