UPDATE locales_npc_text SET entry = 7037, Text0_0_loc2 = 'Les voies de Jaedenar sont aussi ténébreuses que malsaines. Faites attention à vos moindres gestes en ce lieu, $C, ou votre âme sera asservie par vos rivaux…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7037;
UPDATE locales_npc_text SET entry = 7038, Text0_0_loc2 = 'Allez-vous-en ! Je tiens conseil avec les seigneurs de l\'Effroi, et nous discutons d\'affaires qui ne vous regardent pas !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7038;
UPDATE locales_npc_text SET entry = 7039, Text0_0_loc2 = '… Un message de Creuseplaie ? Que peut-il me dire ? Aurait-il besoin de son serviteur et conseiller, Ulathek ? Requiert-il ma présence ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7039;
UPDATE locales_npc_text SET entry = 7040, Text0_0_loc2 = 'Quoi ? Mais comment ? Non ! Ce n\'est pas possible ! J\'ai été si prudent… Il ne peut pas le savoir !$B$BVous mentez ! Vous mentez, et vous allez mourir pour cela !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7040;
UPDATE locales_npc_text SET entry = 7041, Text0_0_loc2 = null, Text0_1_loc2 = 'Quelle belle journée pour être en vie ! Admirez la beauté qui vous entoure, mon $g ami : amie ;. Savourez ce que la vie a à vous donner !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7041;
