UPDATE locales_npc_text SET entry = 7043, Text0_0_loc2 = 'De la lave ? N\'y faites pas attention, mon $g ami : amie ;. Regardez autour de vous. Savourez les joies qu\'offre la nature.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7043;
UPDATE locales_npc_text SET entry = 7044, Text0_0_loc2 = 'Je me rends en Silithus chaque année vers cette époque. Le climat fait un bien fou à mes cors aux pieds.$B$BPromenons-nous un peu, mon $g ami : amie;. Profitons du paysage et tout ça.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7044;
UPDATE locales_npc_text SET entry = 7045, Text0_0_loc2 = 'C’est merveilleux de voir une autre personne dans cette région éloignée de tout. Venez, marchons ensemble. Nos corps se tiendront chaud.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7045;
UPDATE locales_npc_text SET entry = 7046, Text0_0_loc2 = 'C’est dans les hauteurs, du côté ouest de la vallée de la Force.', Text0_1_loc2 = 'C’est dans les hauteurs, du côté ouest de la vallée de la Force.', Text1_0_loc2 = 'C\'est le Hall des Légendes que vous cherchez. Il est très haut dans la Vallée de la Force, du côté ouest.', Text1_1_loc2 = 'C\'est le Hall des Légendes que vous cherchez. Il est très haut dans la Vallée de la Force, du côté ouest.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7046;
UPDATE locales_npc_text SET entry = 7047, Text0_0_loc2 = 'Alors comme ça, vous voilà « Défenseur de l’Alliance », hein ? Bon, je suppose que vous allez vouloir faire un tour au Hall des Champions. C’est dans la Vieille Ville.', Text0_1_loc2 = 'Alors comme ça, vous voilà « Défenseur de l’Alliance », hein ? Bon, je suppose que vous allez vouloir faire un tour au Hall des Champions. C’est dans la Vieille Ville.', Text1_0_loc2 = 'Eh bien eh bien, on fait son chemin dans le monde, on dirait ? C\'est le Hall des Champions que vous cherchez. Vous le trouverez à l’extrémité sud-est de la Vieille ville. Bonne chance !', Text1_1_loc2 = 'Eh bien eh bien, on fait son chemin dans le monde, on dirait ? C\'est le Hall des Champions que vous cherchez. Vous le trouverez à l’extrémité sud-est de la Vieille ville. Bonne chance !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7047;
UPDATE locales_npc_text SET entry = 7055, Text0_0_loc2 = 'Si vous êtes à la recherche du meilleur travail de ce continent et de tous les autres, vous êtes au bon endroit !$B$BJe suis Grimand Elmore, à votre service !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7055;
UPDATE locales_npc_text SET entry = 7056, Text0_0_loc2 = 'Pour invoquer un destrier de l\'effroi, vous devez d\'abord créer un Cercle d\'invocation supérieure, indispensable pour ouvrir un portail vers Xoroth.$B$BUn Cercle d\'invocation supérieure nécessite trois objets puissants : une Cloche de Dethmoora, une Roue de la Marche noire et une Chandelle d\'apocalypse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7056;
UPDATE locales_npc_text SET entry = 7057, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7057;
UPDATE locales_npc_text SET entry = 7074, Text0_0_loc2 = 'Arr ! Il n\'y a rien que j\'aime mieux que de voir des gens se battre à mort dans l\'arène. Si vous êtes là au bon moment, vous pourrez être du nombre !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7074;
UPDATE locales_npc_text SET entry = 7094, Text0_0_loc2 = 'Ouais, ch’est vrai, ch’suis aussi aveugle qu’une chauve-chouris. Et ch’suis aussi soûl qu’un… qu’un truc qu’est vraiment soûl.$B$BMais ch’vais vous dire, $r. Ch’est la bibine qui ag… aiguise mes sens. Tout le monde sait que quand vous perdez un sens, les autres deviennent meilleurs. Hé ben, mon sens de l’alcool est d’venu incroyab’.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7094;
UPDATE locales_npc_text SET entry = 7095, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons choisi de quitter l’abri confortable des Profondeurs pour exploiter… heu, pardon, je veux dire pour EXPLORER le monde.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7095;
UPDATE locales_npc_text SET entry = 7114, Text0_0_loc2 = 'Après tout, ce n\'était peut-être pas le meilleur emplacement pour une boutique…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7114;
UPDATE locales_npc_text SET entry = 7115, Text0_0_loc2 = 'On me nomme Brûlotin. Je suis forgeron… et puis, euh… je vends un peu d’eau et de nourriture en plus. Vous voulez un peu de viande ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7115;
UPDATE locales_npc_text SET entry = 7116, Text0_0_loc2 = 'C’est un plaisir de vous revoir, $N ! Je ne sais pas trop combien de temps je vais m’acharner à gérer une boutique au milieu de nulle part. Mais bon, comme vous m’avez aidé par le passé, je vais vous montrer des trucs qui pourraient vous intéresser !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7116;
UPDATE locales_npc_text SET entry = 7117, Text0_0_loc2 = 'Quand Lolo veut pas renverser sa bière ou salir sa jolie p’tite robe, c’est moi qu’on appelle. Ouaip. Le vieil Hansel est le nain à qui on d’mande de dispenser un p’tit peu de justice… dans la douleur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7117;
UPDATE locales_npc_text SET entry = 7120, Text0_0_loc2 = 'En tant que sous-chef intérimaire de cette installation, j’ai le devoir de veiller à ce que toute activité, affaires, travaux, repos et détente comprennent une dose non négligeable de complications lassantes.$B$BSi vous êtes $gconfronté:confrontée; à une situation qui vous paraît trop simple ou dépourvue de tracas, merci de m’en aviser immédiatement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7120;
UPDATE locales_npc_text SET entry = 7121, Text0_0_loc2 = 'Vous avez accompli toutes les tâches demandées, $N. Vous n’avez plus rien à faire ici. Si vous souhaitez continuer votre carrière comme agent de la Confrérie du thorium, allez parler à Lokhtos Sombrescompte. Vous le trouverez au Sinistre écluseur, dans les profondeurs de Rochenoire.$B$BAdieu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7121;
UPDATE locales_npc_text SET entry = 7122, Text0_0_loc2 = null, Text0_1_loc2 = 'Confrérie du Thorium ou pas, si un de ces Sombrefer s’approche trop, il va se prendre un bon coup de pioche sur le coin de la figure ! Bon, où puis-je vous envoyer, $G mon gars:ma fille; ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7122;
UPDATE locales_npc_text SET entry = 7123, Text0_0_loc2 = null, Text0_1_loc2 = 'Tant que ces nains restent là où ils sont, il n’y aura pas de problème. Où allez-vous, $N ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7123;
