UPDATE locales_npc_text SET entry = 7154, Text0_0_loc2 = 'L\'empreinte d\'une grande main humanoïde est incrustée à la surface de l\'orbe. Malgré d\'incessantes manipulations, l\'orbe reste sans tache.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7154;
UPDATE locales_npc_text SET entry = 7155, Text0_0_loc2 = 'Les marques de l\'orbe correspondent à celles sur votre main.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7155;
UPDATE locales_npc_text SET entry = 7156, Text0_0_loc2 = 'J\'ai... J\'ai échoué, $N. Le seigneur du clan... du clan Rochenoire est trop puissant. Il a facilement consumé mon... mon énergie. Je vous... Je... Tuez-moi... Il le faut... Tuez-moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7156;
UPDATE locales_npc_text SET entry = 7174, Text0_0_loc2 = 'Je suis celui qui garde. Je suis le poing du châtiment. Celui qui soumet les récalcitrants. Osez-vous défier le Chef de guerre ? Osez-vous faire face à mon jugement sans pitié ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7174;
UPDATE locales_npc_text SET entry = 7175, Text0_0_loc2 = 'Vous ne voyez pas que je pêche ici ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Vous avez effrayé les poissons ! Qu\'est-ce que vous voulez ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Que puis-je faire pour vous, $c ?', Text2_1_loc2 = null, Text3_0_loc2 = 'Je resterai ici jusqu\'à ce que je prenne un mérou de 31 kilos ! Mais vous, vous voulez sûrement voler quelque part, n\'est-ce pas ?', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7175;
UPDATE locales_npc_text SET entry = 7176, Text0_0_loc2 = 'Regardez ce dragon à terre, $R. La puissance de la Horde est incontestable !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7176;
