UPDATE locales_npc_text SET entry = 7183, Text0_0_loc2 = 'Un elfe de la nuit dénommé Naralex a récemment découvert qu\'un réseau de cavernes souterraines se dissimulait au cœur des Tarides. Croyant qu\'il pouvait utiliser les sources de ces cavernes pour rendre leur fertilité aux Tarides, le druide commença à aspirer l\'énergie du légendaire Rêve d\'émeraude. Mais sa vision s\'est transformée en cauchemar, et peu à peu les Cavernes des lamentations sont devenues le repaire de prédateurs cruels et dangereux. On dit que Naralex réside encore dans ce labyrinthe, emprisonné derrière les barrières du Rêve d\'émeraude.', Text0_1_loc2 = 'Un elfe de la nuit dénommé Naralex a récemment découvert qu\'un réseau de cavernes souterraines se dissimulait au cœur des Tarides. Croyant qu\'il pouvait utiliser les sources de ces cavernes pour rendre leur fertilité aux Tarides, le druide commença à aspirer l\'énergie du légendaire Rêve d\'émeraude. Mais sa vision s\'est transformée en cauchemar, et peu à peu les Cavernes des lamentations sont devenues le repaire de prédateurs cruels et dangereux. On dit que Naralex réside encore dans ce labyrinthe, emprisonné derrière les barrières du Rêve d\'émeraude.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7183;
