UPDATE locales_npc_text SET entry = 7186, Text0_0_loc2 = null, Text0_1_loc2 = 'Oui, vous là-bas ! Si vous voulez gagner le respect des Sombrelances, pourquoi ne pas commencer par donner des ressources de valeur à ceux qui en ont besoin ? Les produits textiles et les bandages ne poussent pas sur les arbres, vous savez.$B$BAu lieu de garder les diverses pièces de tissu que vous trouvez lors de vos aventures, donnez-les ! Cet acte vous vaudra la reconnaissance des Sombrelances !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7186;
