UPDATE locales_npc_text SET entry = 7188, Text0_0_loc2 = null, Text0_1_loc2 = 'En ces temps difficiles, un simple don de tissu peut être un grand geste pour aider ceux qui ont moins de chance que nous. Le tissu permet de confectionner vêtements et bandages, tous deux très importants pour l\'effort de guerre !$B$BSi vous souhaitez faire don de pièces de tissu aux orcs d\'Orgrimmar, je les accepterai avec plaisir et ferai en sorte que vous receviez la reconnaissance que vous méritez pour cet acte.$B$BPour la Horde !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7188;
UPDATE locales_npc_text SET entry = 7189, Text0_0_loc2 = 'Vous, ici... ce serait dans votre intérêt d\'aider les Réprouvés en ces temps difficiles. Écoutez attentivement, parce que je n\'aime pas avoir à me répéter.$B$BNous nous trouvons en ce moment en grand manque de tissu. Nos stocks de bandages et de produits textiles simples s\'affaiblissent, et nous sommes à la recherche de dons pour les soutenir. Vous ne recevrez pas de compensation financière (voilà pourquoi nous parlons de dons), mais votre aide ne passera pas inaperçue.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7189;
UPDATE locales_npc_text SET entry = 7190, Text0_0_loc2 = 'Salut, $gfrère:sœur;... accordez-moi un moment, s\'il vous plaît.$B$BNous, le peuple tauren, avons beaucoup souffert et traversé bien des épreuves pour établir notre patrie. Nous nous trouvons maintenant en grand manque de tissu. Si vous possédez des pièces de tissu superflues, je vous demande de bien vouloir nous en faire don. Votre aide vous vaudra la reconnaissance de tous les taurens !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7190;
