UPDATE locales_npc_text SET entry = 7193, Text0_0_loc2 = 'Salut $gami:amie;, accordez-moi un instant, s\'il vous plaît...$B$BNous avons beaucoup souffert et traversé bien des épreuves pour établir Teldrassil. Comme si les luttes permanentes de notre époque ne suffisaient pas, nous nous trouvons actuellement en grand manque de tissu. Si vous possédez des pièces de tissu superflues, je vous demande de bien vouloir nous en faire don. Votre aide vous vaudra la reconnaissance de tous les elfes de la nuit !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7193;
UPDATE locales_npc_text SET entry = 7194, Text0_0_loc2 = 'Les exilés de Gnomeregan se trouvent dans une situation plus difficile encore en raison de nos réserves très basses de tissu ! Vous pouvez peut-être nous aider, hein ?!$B$BApportez-moi vos pièces de tissu superflues pour nous aider à reconstituer nos stocks. En les donnant, vous gagnerez le respect et la confiance de tous les gnomes. Voilà quelque chose qui n\'a pas de prix ! Enfin, le tissu en a un, mais là n\'est pas la question...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7194;
