UPDATE locales_npc_text SET entry = 7206, Text0_0_loc2 = 'Le Monastère écarlate se trouve dans les collines au nord-est des Clairières de Tirisfal.', Text0_1_loc2 = 'Le Monastère écarlate se trouve dans les collines au nord-est des Clairières de Tirisfal.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7206;
UPDATE locales_npc_text SET entry = 7207, Text0_0_loc2 = 'Le monastère était autrefois un lieu d\'instruction et de prière. Lors de l\'arrivée du Fléau mort-vivant durant la Troisième guerre, le paisible monastère fut converti en forteresse par les fanatiques de la Croisade écarlate. Les croisés détestent toutes les races non-humaines, quelle que soit leur alliance ou leur affiliation. Ils pensent que tous les étrangers sont des porteurs potentiels de la maladie des morts-vivants… et qu\'ils doivent être détruits.', Text0_1_loc2 = 'Le monastère était autrefois un lieu d\'instruction et de prière. Lors de l\'arrivée du Fléau mort-vivant durant la Troisième guerre, le paisible monastère fut converti en forteresse par les fanatiques de la Croisade écarlate. Les croisés détestent toutes les races non-humaines, quelle que soit leur alliance ou leur affiliation. Ils pensent que tous les étrangers sont des porteurs potentiels de la maladie des morts-vivants… et qu\'ils doivent être détruits.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7207;
UPDATE locales_npc_text SET entry = 7208, Text0_0_loc2 = 'Les Souilles de Tranchebauge se trouvent à l\'extrémité sud des Tarides, du côté est de la route.', Text0_1_loc2 = 'Les Souilles de Tranchebauge se trouvent à l\'extrémité sud des Tarides, du côté est de la route.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7208;
UPDATE locales_npc_text SET entry = 7209, Text0_0_loc2 = 'Construites dans les mêmes lianes que le Kraal de Tranchebauge, les Souilles de Tranchebauge sont la capitale traditionnelle du peuple huran. Ce gigantesque labyrinthe d\'épineux abrite une véritable armée de hurans loyaux ainsi que leurs grands prêtres : l\'ordre de la Tête de Mort. Cependant, une ombre s\'est récemment étendue sur leurs repaires sommaires. Des agents du Fléau – dirigés par la liche Amnennar le Porte-froid – ont pris le contrôle du peuple des hurans, et ont transformé le labyrinthe d\'épineux en un bastion des morts-vivants.', Text0_1_loc2 = 'Construites dans les mêmes lianes que le Kraal de Tranchebauge, les Souilles de Tranchebauge sont la capitale traditionnelle du peuple huran. Ce gigantesque labyrinthe d\'épineux abrite une véritable armée de hurans loyaux ainsi que leurs grands prêtres : l\'ordre de la Tête de Mort. Cependant, une ombre s\'est récemment étendue sur leurs repaires sommaires. Des agents du Fléau – dirigés par la liche Amnennar le Porte-froid – ont pris le contrôle du peuple des hurans, et ont transformé le labyrinthe d\'épineux en un bastion des morts-vivants.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7209;
