UPDATE locales_npc_text SET entry = 7212, Text0_0_loc2 = 'Uldaman est l\'ancien domaine des titans, enterré sous des tonnes de terre depuis la création du monde. Les fouilles menées par les nains dans cette cité ont récemment libéré les premières créations manquées des titans : les troggs. Selon les légendes, les titans estimèrent que l\'expérience des troggs était un échec. Mais ils firent une nouvelle tentative, qui leur permit de créer la race des nains. Ces secrets sont inscrits sur les mythiques Disques de Norgannon – d\'immenses artéfacts qui reposent dans les profondeurs de l\'ancienne cité, protégés par des créatures géantes façonnées dans de la pierre vivante.', Text0_1_loc2 = 'Uldaman est l\'ancien domaine des titans, enterré sous des tonnes de terre depuis la création du monde. Les fouilles menées par les nains dans cette cité ont récemment libéré les premières créations manquées des titans : les troggs. Selon les légendes, les titans estimèrent que l\'expérience des troggs était un échec. Mais ils firent une nouvelle tentative, qui leur permit de créer la race des nains. Ces secrets sont inscrits sur les mythiques Disques de Norgannon – d\'immenses artéfacts qui reposent dans les profondeurs de l\'ancienne cité, protégés par des créatures géantes façonnées dans de la pierre vivante.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7212;
