UPDATE locales_npc_text SET entry = 7241, Text0_0_loc2 = 'C\'est tellement agréable de pêcher. Vous voyez ? Je dois cet état de béatitude à mon mentor, le plus grand pêcheur du monde entier, Nat Pagle. Avez-vous entendu parler de lui ?$B$BSi vous cherchez l\'illumination, Nat Pagle sera celui qui vous montrera la voie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7241;
UPDATE locales_npc_text SET entry = 7242, Text0_0_loc2 = 'Zul\'jin reviendra un jour, $r. Croyez-moi. En attendant, nous maintenons tous nos positions et faisons notre possible pour préserver la population des trolls de la forêt.$B$BCela dit, en tant que maître veneur du village des Vengebroches, il y va de ma responsabilité de m\'assurer que le monde qui nous entoure est sûr et habitable.$B$BLes bêtes de cette région doivent être contenues.$B$BCherchez-vous du travail ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 7242;
